import React, { useEffect, useRef, useState } from "react";
import * as faceapi from "face-api.js";
import "./FaceRegistration.css";

export default function FaceRegistration({ user, onComplete }) {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [captureStatus, setCaptureStatus] = useState("idle"); // idle | capturing | captured | saved
  const [message, setMessage] = useState("Loading face detection models...");
  const [captureCount, setCaptureCount] = useState(0);
  const [capturedDescriptor, setCapturedDescriptor] = useState(null);

  const detectionInterval = useRef(null);

  // Load face-api models on mount
  useEffect(() => {
    const loadModels = async () => {
      try {
        const MODEL_URL = process.env.PUBLIC_URL + "/models";
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        ]);
        setModelsLoaded(true);
        setMessage("Models loaded. Start your camera.");
      } catch (err) {
        console.error(err);
        setMessage("Failed to load face detection models.");
      }
    };
    loadModels();
    return () => stopCamera();
  }, []);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 640, height: 480 } });
      videoRef.current.srcObject = stream;
      setCameraStarted(true);
      setMessage("Camera started. Position your face and click 'Start Capture'.");
    } catch (err) {
      console.error(err);
      setMessage("Cannot access camera. Please allow webcam permissions.");
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    setCameraStarted(false);
  };

  const startCapture = () => {
    if (!cameraStarted || !modelsLoaded) return;
    setCaptureStatus("capturing");
    setMessage("Capturing face samples. Keep your face steady...");
    setCaptureCount(0);

    const descriptors = [];

    detectionInterval.current = setInterval(async () => {
      if (!videoRef.current) return;

      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (detection) {
        descriptors.push(Array.from(detection.descriptor));
        setCaptureCount(descriptors.length);

        // Draw face on canvas
        if (canvasRef.current) {
          const dims = faceapi.matchDimensions(canvasRef.current, videoRef.current, true);
          const resized = faceapi.resizeResults(detection, dims);
          const ctx = canvasRef.current.getContext("2d");
          ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
          faceapi.draw.drawDetections(canvasRef.current, resized);
          faceapi.draw.drawFaceLandmarks(canvasRef.current, resized);
        }

        if (descriptors.length >= 5) {
          clearInterval(detectionInterval.current);
          const avgDescriptor = averageDescriptors(descriptors);
          setCapturedDescriptor(avgDescriptor);
          setCaptureStatus("captured");
          setMessage("Face captured successfully! Click 'Save Face'.");
        }
      } else {
        setMessage("No face detected. Please position your face clearly.");
      }
    }, 500);
  };

  const averageDescriptors = (descriptors) => {
    const avg = new Array(128).fill(0);
    descriptors.forEach(desc => desc.forEach((val, i) => (avg[i] += val)));
    return avg.map(val => val / descriptors.length);
  };

  const saveFace = async () => {
    if (!capturedDescriptor) return;

    setCaptureStatus("saving");
    setMessage("Saving face data...");

    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:4000/api/auth/register-face", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ faceDescriptor: capturedDescriptor }),
      });

      if (res.ok) {
        setCaptureStatus("saved");
        setMessage("Face registered successfully!");
        stopCamera();
        if (onComplete) setTimeout(() => onComplete(), 2000);
      } else {
        throw new Error("Failed to register face");
      }
    } catch (err) {
      console.error(err);
      setCaptureStatus("captured");
      setMessage("Error saving face. Try again.");
    }
  };

  const retryCapture = () => {
    setCaptureStatus("idle");
    setMessage("Position your face and click 'Start Capture'.");
    setCaptureCount(0);
    setCapturedDescriptor(null);
    if (canvasRef.current) {
      const ctx = canvasRef.current.getContext("2d");
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    }
  };

  return (
    <div className="face-registration-container">
      <h2>ðŸ“¸ Face Registration</h2>
      <p>{message}</p>

      <div className="camera-container">
        <video ref={videoRef} autoPlay muted width="320" height="240" />
        <canvas ref={canvasRef} width="320" height="240" />
      </div>

      <p>Captured samples: {captureCount}/5</p>

      <div className="action-buttons">
        {!cameraStarted && <button onClick={startCamera}>Start Camera</button>}
        {cameraStarted && captureStatus === "idle" && (
          <button onClick={startCapture} disabled={!modelsLoaded}>Start Capture</button>
        )}
        {captureStatus === "captured" && <button onClick={saveFace}>Save Face</button>}
        {["captured", "saved"].includes(captureStatus) && <button onClick={retryCapture}>Retry</button>}
      </div>
    </div>
  );
}
