import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import { useNavigate, useParams } from "react-router-dom";
import "./ExamPage.css";

export default function ExamPage({ user, onLogout }) {
  const { examId } = useParams();
  const videoRef = useRef(null);
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);
  const [timeLeft, setTimeLeft] = useState(600);
  const [logs, setLogs] = useState([]);
  const [paper, setPaper] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showSummary, setShowSummary] = useState(false);
  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);
  const navigate = useNavigate();

  // Progress tracking
  const answeredCount = answers.filter(a => a !== null).length;
  const progressPercentage = (answeredCount / (paper?.questions?.length || 1)) * 100;

  const goToQuestion = (index) => {
    setCurrentQuestion(index);
    setShowSummary(false);
  };

  const nextQuestion = () => {
    if (currentQuestion < paper.questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowSummary(true);
    }
  };

  const previousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
    setShowSummary(false);
  };

  // --- Load exam & models ---
  useEffect(() => {
    const loadModelsAndExam = async () => {
      try {
        const MODEL_URL = process.env.PUBLIC_URL + "/models";
        await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);

        const res = await fetch(`http://localhost:4000/api/exam/paper/${examId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        if (!res.ok) throw new Error(`Server responded with ${res.status}`);
        const data = await res.json();

        setPaper(data);
        setAnswers(new Array(data.questions.length).fill(null));
        setTimeLeft((data.durationMins || 10) * 60);

        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: false });
        if (videoRef.current) videoRef.current.srcObject = stream;
      } catch (err) {
        console.error("Failed to load exam:", err);
        alert("Failed to load exam. Returning to home.");
        navigate("/home");
      }
    };

    loadModelsAndExam();
  }, [examId, navigate]);

  // --- Tab switch detection ---
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setTabSwitchCount(prev => prev + 1);
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  // --- Warnings for tab switch ---
  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("⚠️ Warning: You switched tabs! One more switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) {
      alert("❌ You switched tabs too many times. Auto-submitting exam.");
      handleSubmit();
    }
  }, [tabSwitchCount, warningShown]);

  // --- Timer countdown ---
  useEffect(() => {
    if (timeLeft <= 0) {
      alert("⏰ Time's up! Auto-submitting exam.");
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  // --- Face detection ---
  useEffect(() => {
    if (!paper) return;
    const interval = setInterval(async () => {
      if (!videoRef.current) return;

      const detections = await faceapi.detectAllFaces(
        videoRef.current,
        new faceapi.TinyFaceDetectorOptions()
      );

      if (detections.length > 1) {
        if (!multipleFaceActive.current) {
          multipleFaceActive.current = true;
          const startTime = new Date();
          startTimeRef.current = startTime;
          setLogs(prev => [...prev, `Multiple faces started at ${startTime.toLocaleTimeString()}`]);
        }

        try {
          await fetch("http://localhost:4000/api/exam/proctor-event", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
            body: JSON.stringify({
              examId,
              type: "multiple-face",
              data: { message: `Detected ${detections.length} faces` },
            }),
          });
        } catch (err) {
          console.error("Error sending multiple face log:", err);
        }
      } else if (multipleFaceActive.current) {
        multipleFaceActive.current = false;
        const endTime = new Date();
        const duration = Math.round((endTime - startTimeRef.current) / 1000);
        setLogs(prev => [
          ...prev,
          `Multiple faces ended at ${endTime.toLocaleTimeString()} (Duration: ${duration}s)`,
        ]);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [paper, examId]);

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${min}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    if (!paper) return alert("Exam data not loaded!");
    if (handleSubmit.isSubmitting) return;

    handleSubmit.isSubmitting = true;
    const confirmSubmit = window.confirm("Are you sure you want to submit?");
    if (!confirmSubmit) {
      handleSubmit.isSubmitting = false;
      return;
    }

    try {
      const response = await fetch("http://localhost:4000/api/exam/submit", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          userId: user._id,
          examId,
          answers,
        }),
      });

      if (!response.ok) throw new Error(`Submit failed: ${response.status}`);
      const data = await response.json();

      alert("✅ Exam submitted successfully!");
      navigate(`/result/${data.submissionId}`);
    } catch (err) {
      console.error(err);
      alert("❌ Failed to submit exam. Please try again.");
      handleSubmit.isSubmitting = false;
    }
  };

  if (!paper) {
    return (
      <div className="container" style={{ textAlign: "center", padding: "3rem" }}>
        <h3>Loading exam...</h3>
        <p>Please wait while we prepare your exam.</p>
      </div>
    );
  }

  // ---- MAIN RETURN ----
  return (
    <div className="exam-page">
      <button 
        onClick={() => {
          if (window.confirm("Are you sure you want to exit?")) {
            navigate("/home");
          }
        }}
        className="exam-back-btn"
      >
        ← Back to Home
      </button>

      <div className="exam-header">
        <h2>📝 {paper.title || "Online Exam"}</h2>
        <div className="exam-timer">⏱️ Time Left: {formatTime(timeLeft)}</div>
      </div>

      {/* Progress Bar + Navigator */}
      <div className="progress-section">
        <div className="progress-bar-container">
          <div 
            className="progress-bar-fill" 
            style={{ width: `${progressPercentage}%` }}
          >
            <span className="progress-text">
              {answeredCount}/{paper.questions.length} Answered
            </span>
          </div>
        </div>

        <div className="question-navigator">
          <h4>Question Navigator</h4>
          <div className="question-grid">
            {paper.questions.map((_, index) => (
              <button
                key={index}
                className={`question-dot ${answers[index] !== null ? 'answered' : ''} ${currentQuestion === index ? 'active' : ''}`}
                onClick={() => goToQuestion(index)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="exam-body">
        <div className="exam-content">
          <div className="question">
            <h3>Q{currentQuestion + 1}: {paper.questions[currentQuestion].text}</h3>
            <div className="options">
              {paper.questions[currentQuestion].options.map((opt, idx) => (
                <label key={idx} className="option-label">
                  <input
                    type="radio"
                    name={`q${currentQuestion}`}
                    checked={answers[currentQuestion] === idx}
                    onChange={() => handleAnswerChange(currentQuestion, idx)}
                  />
                  {opt}
                </label>
              ))}
            </div>
          </div>

          <div className="nav-buttons">
            <button onClick={previousQuestion} disabled={currentQuestion === 0}>⬅ Prev</button>
            <button onClick={nextQuestion}>
              {currentQuestion === paper.questions.length - 1 ? "Submit" : "Next ➡"}
            </button>
          </div>
        </div>

        <div className="sidebar">
          <div className="camera-preview">
            <h4>📷 Camera Monitor</h4>
            <video ref={videoRef} autoPlay muted />
            <p>🎥 Camera Active</p>
          </div>

          <div className="face-logs">
            <h4>👀 Face Detection Logs</h4>
            {logs.length > 0 ? (
              <ul>
                {logs.map((log, i) => (
                  <li key={i}>{log}</li>
                ))}
              </ul>
            ) : (
              <p>✅ No issues detected</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
