import React, { useState, useRef, useEffect } from "react";
import * as faceapi from "face-api.js";
import { authApi } from "../api"; // your existing API helper
import "./RegisterPage.css";

export default function RegisterPage({ onRegistered }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const [secretKey, setSecretKey] = useState(""); // admin
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const [registeredUser, setRegisteredUser] = useState(null);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [status, setStatus] = useState("initial"); // initial, ready, capturing, captured, success, error
  const [captureCount, setCaptureCount] = useState(0);
  const [capturedDescriptor, setCapturedDescriptor] = useState(null);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const detectionInterval = useRef(null);

  // --- Load face-api models ---
  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = "/models";
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);
      setModelsLoaded(true);
    };
    loadModels();
  }, []);

  // --- Start camera ---
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 640, height: 480 } });
      videoRef.current.srcObject = stream;
      setStatus("ready");
    } catch (err) {
      console.error(err);
      alert("Cannot access camera.");
    }
  };

  // --- Stop camera ---
  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  // --- Average descriptors ---
  const averageDescriptors = (descriptors) => {
    const avg = new Array(128).fill(0);
    descriptors.forEach(desc => {
      desc.forEach((val, idx) => avg[idx] += val);
    });
    return avg.map(val => val / descriptors.length);
  };

  // --- Capture face samples ---
  const startCapture = () => {
    if (!registeredUser) {
      alert("Register first to capture face!");
      return;
    }

    setStatus("capturing");
    setCaptureCount(0);
    const descriptors = [];

    detectionInterval.current = setInterval(async () => {
      if (!videoRef.current) return;
      try {
        const detection = await faceapi
          .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          descriptors.push(Array.from(detection.descriptor));
          setCaptureCount(prev => prev + 1);

          // Draw on canvas
          if (canvasRef.current) {
            const dims = faceapi.matchDimensions(canvasRef.current, videoRef.current, true);
            const resized = faceapi.resizeResults(detection, dims);
            const ctx = canvasRef.current.getContext("2d");
            ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
            faceapi.draw.drawDetections(canvasRef.current, resized);
            faceapi.draw.drawFaceLandmarks(canvasRef.current, resized);
          }

          if (descriptors.length >= 5) {
            clearInterval(detectionInterval.current);
            const avgDesc = averageDescriptors(descriptors);
            setCapturedDescriptor(avgDesc);
            setStatus("captured");
          }
        }
      } catch (err) {
        console.error("Detection error:", err);
      }
    }, 500);
  };

  // --- Send face to backend ---
  const saveFace = async () => {
    if (!capturedDescriptor) return;
    setStatus("saving");
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:4000/api/auth/register-face", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ userId: registeredUser._id, faceDescriptor: capturedDescriptor }),
      });
      if (res.ok) {
        setStatus("success");
        stopCamera();
        alert("âœ… Face registered successfully!");
      } else {
        throw new Error("Face registration failed");
      }
    } catch (err) {
      console.error(err);
      setStatus("error");
      alert("Failed to save face. Try again.");
    }
  };

  // --- Register user ---
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      const res = await authApi.register({ name, email, password, role, secretKey });
      setRegisteredUser(res.data.user);
      setMessage("User registered! Now capture your face.");
      if (onRegistered) onRegistered();
    } catch (err) {
      setMessage(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Register</h2>
        <form onSubmit={handleRegister} className="register-form">
          <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          <select value={role} onChange={e => setRole(e.target.value)}>
            <option value="student">Student</option>
            <option value="admin">Admin</option>
          </select>
          {role === "admin" && <input type="password" placeholder="Admin Secret Key" value={secretKey} onChange={e => setSecretKey(e.target.value)} required />}
          {message && <div className="register-error">{message}</div>}
          <button type="submit" disabled={loading}>{loading ? "Registering..." : "Register"}</button>
        </form>

        {/* Face Capture Section */}
        {registeredUser && (
          <div className="face-registration">
            <h3>ðŸŽ¥ Face Capture</h3>
            <video ref={videoRef} autoPlay width="320" height="240" />
            <canvas ref={canvasRef} width="320" height="240" />
            <div className="face-btns">
              <button type="button" onClick={startCamera} disabled={!modelsLoaded || status === "capturing"}>Start Capture</button>
              <button type="button" onClick={saveFace} disabled={status !== "captured"}>Save Face</button>
            </div>
            {status === "capturing" && <p>Capturing: {captureCount}/5</p>}
          </div>
        )}
      </div>
    </div>
  );
}
