import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function ResultPage() {
  const { submissionId } = useParams();
  const [result, setResult] = useState(null);
  const [faceLogs, setFaceLogs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchResult = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch exam result
        const response = await fetch(`http://localhost:4000/api/exam/result/${submissionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await response.json();
        setResult(data);

        // Fetch face logs
        const faceRes = await fetch(
          `http://localhost:4000/api/exam/face-logs/${data.userId}/${data.examId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        const faceData = await faceRes.json();
        setFaceLogs(faceData.faceLogs || []);
      } catch (error) {
        console.error("Error fetching result:", error);
      }
    };

    fetchResult();
  }, [submissionId]);

  if (!result) return <div className="p-4">Loading your result...</div>;

  return (
    <div className="result-container" style={{ padding: "2rem", maxWidth: "700px", margin: "auto" }}>
      <h2>Exam Result</h2>
      <p>
        <strong>Total Score:</strong> {result.score}/{result.totalQuestions}
      </p>

      <hr />
      {result.questions.map((q, i) => {
        const userAnswerIndex = result.answers[i];
        const isCorrect = userAnswerIndex === q.correctOption;

        return (
          <div key={i} style={{ marginBottom: "1.5rem" }}>
            <p><strong>Q{i + 1}:</strong> {q.text}</p>
            <p>Your Answer: {q.options[userAnswerIndex] || "Not Answered"}</p>
            <p>Correct Answer: {q.options[q.correctOption]}</p>
            <p>
              {isCorrect ? (
                <span style={{ color: "green" }}>✅ Correct</span>
              ) : (
                <span style={{ color: "red" }}>❌ Wrong</span>
              )}
            </p>
            <hr />
          </div>
        );
      })}

      <div style={{ marginTop: "2rem" }}>
        <h3>👀 Multiple Face Detection Logs</h3>
        {faceLogs.length > 0 ? (
          <ul>
            {faceLogs.map((log, index) => (
              <li key={index}>
                {new Date(log.ts).toLocaleString()} — Multiple faces detected!
              </li>
            ))}
          </ul>
        ) : (
          <p style={{ color: "gray" }}>No multiple face incidents detected 🎉</p>
        )}
      </div>

      <button
        onClick={() => navigate("/")}
        style={{
          background: "#007bff",
          color: "white",
          padding: "0.5rem 1rem",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          marginTop: "1.5rem",
        }}
      >
        Go to Home
      </button>
    </div>
  );
}
