import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import "../styles.css";
import SubmissionPage from "./SubmissionPage";

export default function ExamPage({ user, onLogout }) {
  const videoRef = useRef(null);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [tabSwitches, setTabSwitches] = useState([]);
  const [multipleFaceLogs, setMultipleFaceLogs] = useState([]);
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);
  const [timeLeft, setTimeLeft] = useState(600); // 10 mins
  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);
  const [showSubmission, setShowSubmission] = useState(false);

  // --- Load models & camera ---
  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = process.env.PUBLIC_URL + "/models";
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      startVideo();
    };
    const startVideo = () => {
      navigator.mediaDevices
        .getUserMedia({ video: true, audio: false })
        .then((stream) => (videoRef.current.srcObject = stream))
        .catch((err) => console.error("Camera error:", err));
    };
    loadModels();
  }, []);

  // --- Fetch exam ---
  useEffect(() => {
    fetch("/api/exam/paper", {
      headers: { Authorization: "Bearer " + localStorage.getItem("token") },
    })
      .then((res) => res.json())
      .then((data) => setQuestions(data.questions))
      .catch((err) => console.error(err));
  }, []);

  // --- Tab switch detection ---
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setTabSwitchCount((prev) => prev + 1);
        setTabSwitches((prev) => [...prev, { timestamp: new Date() }]);
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("⚠️ Warning: You switched tabs! One more switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) {
      alert("❌ You switched tabs too many times. Auto-submitting exam.");
      handleSubmit();
    }
  }, [tabSwitchCount]);

  // --- Timer ---
  useEffect(() => {
    if (timeLeft <= 0) {
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  // --- Multiple face detection ---
  useEffect(() => {
    const interval = setInterval(async () => {
      if (!videoRef.current) return;

      const detections = await faceapi.detectAllFaces(
        videoRef.current,
        new faceapi.TinyFaceDetectorOptions()
      );

      if (detections.length > 1) {
        if (!multipleFaceActive.current) {
          multipleFaceActive.current = true;
          startTimeRef.current = new Date();
        }
      } else if (multipleFaceActive.current) {
        multipleFaceActive.current = false;
        const endTime = new Date();
        const duration = Math.round((endTime - startTimeRef.current) / 1000);
        setMultipleFaceLogs((prev) => [...prev, { startTime: startTimeRef.current, endTime, duration }]);
      }
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  // --- Handle answer selection ---
  const handleAnswer = (questionId, selectedOption) => {
    setAnswers((prev) => {
      const existing = prev.find((a) => a.questionId === questionId);
      if (existing) {
        return prev.map((a) => a.questionId === questionId ? { questionId, selectedOption } : a);
      } else {
        return [...prev, { questionId, selectedOption }];
      }
    });
  };

  // --- Submit ---
  const handleSubmit = async () => {
    try {
      const submissionData = { userId: user._id, username: user.name, examId: "EXAM001", answers, tabSwitches, multipleFaceLogs };
      await fetch("/api/exam/submitExam", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: "Bearer " + localStorage.getItem("token") },
        body: JSON.stringify(submissionData),
      });
      setShowSubmission(true);
    } catch (err) {
      console.error(err);
      alert("Error submitting exam. Try again.");
    }
  };

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${min}:${sec < 10 ? "0" : ""}${sec}`;
  };

  if (showSubmission) return <SubmissionPage user={user} examId="EXAM001" onBack={() => setShowSubmission(false)} />;

  return (
    <div className="container exam-page">
      <div className="exam-header">
        <h2>Online Exam Portal</h2>
        <button onClick={onLogout} style={{ float: "right" }}>Logout</button>
        <div className="timer">⏱️ Time Left: {formatTime(timeLeft)}</div>
      </div>

      <div className="exam-body">
        <div className="exam-content">
          {questions.map((q) => (
            <div key={q.id} className="question">
              <h3>{q.q}</h3>
              <div className="options">
                {q.options.map((opt, idx) => (
                  <label key={idx}>
                    <input
                      type="radio"
                      name={`q${q.id}`}
                      checked={answers.find(a => a.questionId === q.id)?.selectedOption === idx}
                      onChange={() => handleAnswer(q.id, idx)}
                    />
                    {opt}
                  </label>
                ))}
              </div>
            </div>
          ))}
          <button onClick={handleSubmit}>Submit Exam</button>
        </div>

        <div className="camera-preview">
          <video ref={videoRef} autoPlay muted style={{ width: "300px", borderRadius: "8px" }} />
          <p>Camera Monitoring Active 🎥</p>
        </div>
      </div>
    </div>
  );
}
