import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import { useNavigate, useParams } from "react-router-dom";
import "./ExamPage.css";

export default function ExamPage({ user, onLogout }) {
  const { examId } = useParams();
  const videoRef = useRef(null);
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);
  const [timeLeft, setTimeLeft] = useState(600);
  const [logs, setLogs] = useState([]);
  const [paper, setPaper] = useState(null);
  const [answers, setAnswers] = useState([]);
  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);
  const isSubmitting = useRef(false);
  const navigate = useNavigate();
   const [currentQ, setCurrentQ] = useState(0);

  // --- Load exam paper & models ---
  useEffect(() => {
    const loadModelsAndExam = async () => {
      try {
        const MODEL_URL = process.env.PUBLIC_URL + "/models";
        await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);

        // Fetch specific exam paper using examId
        const res = await fetch(
          `http://localhost:4000/api/exam/paper/${examId}`,
          {
            headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
          }
        );

        if (!res.ok) throw new Error(`Server responded with status ${res.status}`);
        const data = await res.json();
        console.log("Exam paper fetched:", data);

        if (!data || !data.questions || !data.questions.length) {
          throw new Error("No questions returned from server");
        }

        setPaper(data);
        setAnswers(new Array(data.questions.length).fill(null));
        setTimeLeft((data.durationMins || 10) * 60);

        // Start camera
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: true, 
          audio: false 
        });
        if (videoRef.current) videoRef.current.srcObject = stream;
      } catch (err) {
        console.error("Failed to load exam:", err);
        alert("Failed to load exam. Returning to home.");
        navigate("/home");
      }
    };

    loadModelsAndExam();
  }, [examId, navigate]);

  // --- Tab switch detection ---
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setTabSwitchCount((prev) => prev + 1);
      }
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  // --- Tab switch warnings and auto-submit ---
  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("‚ö†Ô∏è Warning: You switched tabs! One more switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) {
      alert("‚ùå You switched tabs too many times. Your exam will be auto-submitted now.");
      handleSubmit();
    }
  }, [tabSwitchCount, warningShown]);

  // --- Timer countdown ---
 useEffect(() => {
    async function startCamera() {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("Camera error:", error);
      }
    }
    startCamera();
  }, []);
  // --- Multiple face detection ---
  useEffect(() => {
    if (!paper) return;

    const interval = setInterval(async () => {
      if (!videoRef.current) return;

      try {
        const detections = await faceapi.detectAllFaces(
          videoRef.current,
          new faceapi.TinyFaceDetectorOptions()
        );

        if (detections.length > 1) {
          if (!multipleFaceActive.current) {
            multipleFaceActive.current = true;
            const startTime = new Date();
            startTimeRef.current = startTime;
            setLogs((prev) => [
              ...prev,
              `Multiple faces started at ${startTime.toLocaleTimeString()}`
            ]);
          }

          // Send event to backend
          try {
            await fetch("http://localhost:4000/api/exam/proctor-event", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("token")}`,
              },
              body: JSON.stringify({
                examId: examId,
                type: "multiple-face",
                data: { message: `Detected ${detections.length} faces` },
              }),
            });
          } catch (err) {
            console.error("Error sending multiple face log:", err);
          }
        } else if (multipleFaceActive.current) {
          multipleFaceActive.current = false;
          const endTime = new Date();
          const duration = Math.round((endTime - startTimeRef.current) / 1000);
          setLogs((prev) => [
            ...prev,
            `Multiple faces ended at ${endTime.toLocaleTimeString()} (Duration: ${duration}s)`,
          ]);
        }
      } catch (error) {
        console.error("Face detection error:", error);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [paper, examId]);

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${min}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    // Prevent multiple submissions
    if (isSubmitting.current) {
      console.log("Already submitting, please wait...");
      return;
    }

    if (!paper) {
      alert("Exam data not loaded!");
      return;
    }

    // Only ask for confirmation if manually submitting (not auto-submit)
    if (tabSwitchCount < 3 && timeLeft > 0) {
      const confirmSubmit = window.confirm("Are you sure you want to submit your exam?");
      if (!confirmSubmit) {
        return;
      }
    }

    isSubmitting.current = true;

    try {
      console.log("Submitting exam...", {
        userId: user._id,
        examId: examId,
        answers: answers
      });

      const response = await fetch("http://localhost:4000/api/exam/submit", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          userId: user._id,
          examId: examId,
          answers: answers,
        }),
      });

      console.log("Response status:", response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Submit failed:", errorText);
        throw new Error(`Submit failed: ${response.status}`);
      }

      const data = await response.json();
      console.log("Submit response:", data);

      if (data.submissionId) {
        alert("‚úÖ Exam submitted successfully!");
        navigate(`/result/${data.submissionId}`);
      } else {
        throw new Error("No submission ID returned");
      }
    } catch (error) {
      console.error("Submit error:", error);
      alert("‚ùå Failed to submit exam. Please try again.");
      isSubmitting.current = false;
    }
  };

  if (!paper || !paper.questions) {
    return (
      <div className="exam-loading">
        <div className="loading-spinner"></div>
        <h3>Loading exam...</h3>
        <p>Please wait while we prepare your exam.</p>
      </div>
    );
  }

  return (
    <div className="exam-page">
      {/* Back button */}
      <button 
        onClick={() => {
          if (window.confirm("Are you sure you want to exit? Your progress will be lost!")) {
            navigate("/home");
          }
        }}
        className="exam-back-btn"
      >
        ‚Üê Back to Home
      </button>

      <div className="exam-header">
        <h2>üìù {paper.title || "Online Exam"}</h2>
        <div className="exam-timer">‚è±Ô∏è Time Left: {formatTime(timeLeft)}</div>
      </div>

      {tabSwitchCount > 0 && (
        <div className="exam-tab-warning">
          <p>
            Tab Switches: <span className="warning-count">{tabSwitchCount}/3</span>
            {tabSwitchCount >= 2 && " ‚ö†Ô∏è Warning! One more will auto-submit!"}
          </p>
        </div>
      )}

      <div className="exam-body">
        <div className="exam-main-content">
          {paper.questions?.map((q, i) => (
            <div key={i} className="exam-question">
              <h3>Q{i + 1}: {q.text}</h3>
              <div className="exam-options">
                {q.options?.map((opt, idx) => (
                  <label 
                    key={idx} 
                    className={`exam-option-label ${answers[i] === idx ? 'selected' : ''}`}
                  >
                    <input
                      type="radio"
                      name={`q${i}`}
                      checked={answers[i] === idx}
                      onChange={() => handleAnswerChange(i, idx)}
                    />
                    {opt}
                  </label>
                ))}
              </div>
            </div>
          ))}
          
          <button 
            onClick={handleSubmit} 
            className="exam-submit-btn"
            disabled={isSubmitting.current}
          >
            {isSubmitting.current ? '‚è≥ Submitting...' : 'üöÄ Submit Exam'}
          </button>
        </div>

        <div className="exam-sidebar">
          <div className="exam-camera-preview">
            <h4>üì∑ Camera Monitor</h4>
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              style={{ width: "100%", maxWidth: "300px", borderRadius: "8px" }}
            />
            <p className="exam-camera-status">üé• Camera Active</p>
          </div>

