import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import { useNavigate, useParams } from "react-router-dom";
import "./ExamPage.css";

export default function ExamPage({ user }) {
  const { examId } = useParams();
  const navigate = useNavigate();
  const videoRef = useRef(null);

  const [paper, setPaper] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [currentQ, setCurrentQ] = useState(0);
  const [timeLeft, setTimeLeft] = useState(600);
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);
  const [logs, setLogs] = useState([]);

  const storedDescriptor = useRef(null);
  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);
  const isSubmitting = useRef(false);

  // --- Load exam & models, start camera ---
  useEffect(() => {
    let faceInterval, multiFaceInterval;
    const MODEL_URL = process.env.PUBLIC_URL + "/models";

    const initExam = async () => {
      try {
        // Load models once
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        ]);

        // Load exam paper
        const res = await fetch(`http://localhost:4000/api/exam/paper/${examId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        if (!res.ok) throw new Error("Failed to load exam");
        const data = await res.json();
        if (!data?.questions?.length) throw new Error("No questions found");
        setPaper(data);
        setAnswers(new Array(data.questions.length).fill(null));
        setTimeLeft((data.durationMins || 10) * 60);

        // Start video
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) videoRef.current.srcObject = stream;

        // Fetch stored face descriptor
        const userRes = await fetch(`http://localhost:4000/api/user/${user._id}`);
        const userData = await userRes.json();
        storedDescriptor.current = new Float32Array(userData.user.faceDescriptor);

        // Start face verification every 5 seconds
        faceInterval = setInterval(verifyFace, 5000);

        // Start multiple face detection every 3 seconds
        multiFaceInterval = setInterval(multiFaceDetection, 3000);

      } catch (err) {
        console.error(err);
        alert("Failed to load exam. Redirecting to dashboard.");
        navigate(user.role === "student" ? "/student/dashboard" : "/admin/dashboard");
      }
    };

    initExam();

    // Cleanup intervals on unmount
    return () => {
      clearInterval(faceInterval);
      clearInterval(multiFaceInterval);
      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, [examId, navigate, user]);

  // --- Face Verification ---
  const verifyFace = async () => {
    if (!videoRef.current || !storedDescriptor.current) return;
    const detection = await faceapi
      .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
      .withFaceLandmarks()
      .withFaceDescriptor();
    if (!detection) return console.log("No face detected");

    const distance = faceapi.euclideanDistance(detection.descriptor, storedDescriptor.current);
    if (distance > 0.6) {
      console.log("üö® Different person detected");
      setLogs(prev => [...prev, `Face mismatch at ${new Date().toLocaleTimeString()}`]);
      // Optional: alert or backend log
    } else {
      console.log("‚úÖ Face verified");
    }
  };

  // --- Multiple Face Detection ---
  const multiFaceDetection = async () => {
    if (!videoRef.current || !paper) return;
    const detections = await faceapi.detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions());
    if (detections.length > 1) {
      if (!multipleFaceActive.current) {
        multipleFaceActive.current = true;
        startTimeRef.current = new Date();
        setLogs(prev => [...prev, `Multiple faces started at ${startTimeRef.current.toLocaleTimeString()}`]);
      }
      // Send event to backend
      await fetch("http://localhost:4000/api/exam/proctor-event", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          examId,
          type: "multiple-face",
          data: { message: `Detected ${detections.length} faces` },
        }),
      });
    } else if (multipleFaceActive.current) {
      multipleFaceActive.current = false;
      const endTime = new Date();
      const duration = Math.round((endTime - startTimeRef.current) / 1000);
      setLogs(prev => [...prev, `Multiple faces ended at ${endTime.toLocaleTimeString()} (Duration: ${duration}s)`]);
    }
  };

  // --- Timer ---
  useEffect(() => {
    if (timeLeft <= 0) return handleSubmit();
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  // --- Tab switch detection ---
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) setTabSwitchCount(prev => prev + 1);
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("‚ö†Ô∏è Warning: One more tab switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) handleSubmit();
  }, [tabSwitchCount, warningShown]);

  // --- Answer handling ---
  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  // --- Submit ---
  const handleSubmit = async () => {
    if (isSubmitting.current || !paper) return;
    if (!window.confirm("Submit your exam?")) return;
    isSubmitting.current = true;

    try {
      const res = await fetch("http://localhost:4000/api/exam/submit", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ userId: user._id, examId, answers }),
      });
      const data = await res.json();
      if (data.submissionId) navigate(`/result/${data.submissionId}`);
      else throw new Error("Submission failed");
    } catch (err) {
      console.error(err);
      alert("Failed to submit exam.");
      isSubmitting.current = false;
    }
  };

  if (!paper) return <h3>Loading exam...</h3>;

  const question = paper.questions[currentQ];
  const formatTime = (seconds) => `${Math.floor(seconds / 60)}:${(seconds % 60).toString().padStart(2, "0")}`;

  return (
    <div className="exam-page">
      <button onClick={() => navigate(user.role === "student" ? "/student/dashboard" : "/admin/dashboard")}>
        ‚Üê Back to Dashboard
      </button>
      <div className="exam-body">
        <div className="exam-main-content">
          <div>Time Left: {formatTime(timeLeft)}</div>
          <div>Question {currentQ + 1} of {paper.questions.length}</div>
          <h3>{question.text}</h3>
          {question.options.map((opt, idx) => (
            <label key={idx}>
              <input
                type="radio"
                checked={answers[currentQ] === idx}
                onChange={() => handleAnswerChange(currentQ, idx)}
              />
              {opt}
            </label>
          ))}
          <div>
            {currentQ > 0 && <button onClick={() => setCurrentQ(currentQ - 1)}>‚¨ÖÔ∏è Previous</button>}
            {currentQ < paper.questions.length - 1 ? (
              <button onClick={() => setCurrentQ(currentQ + 1)}>Next ‚û°Ô∏è</button>
            ) : (
              <button onClick={handleSubmit}>Submit</button>
            )}
          </div>
        </div>
        <div className="exam-sidebar">
          <video ref={videoRef} autoPlay muted width="300" />
          <div>
            <h4>Face Detection Logs</h4>
            {logs.length > 0 ? <ul>{logs.map((l, i) => <li key={i}>{l}</li>)}</ul> : <p>No issues detected ‚úÖ</p>}
          </div>
        </div>
      </div>
    </div>
  );
}
