import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import ExamPage from './components/ExamPage';
import AdminDashboard from "./components/AdminDashboard";

import { setAuthToken } from './api';
import './styles.css';

function App() {
  const [user, setUser] = useState(null);

  // Load token from localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const name = localStorage.getItem('name');
    if (token) {
      setAuthToken(token);
      setUser({ name });
    }
  }, []);

  // Handle login
  const handleLogin = (token, name) => {
    localStorage.setItem('token', token);
    localStorage.setItem('name', name);
    setAuthToken(token);
    setUser({ name });
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('name');
    setAuthToken(null);
    setUser(null);
  };

  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route
          path="/login"
          element={!user ? <LoginPage onLogin={handleLogin} /> : <Navigate to="/exam" />}
        />
        <Route
          path="/register"
          element={!user ? <RegisterPage onRegistered={() => {}} /> : <Navigate to="/exam" />}
        />

        {/* Protected Exam Page */}
        <Route
          path="/exam"
          element={user ? <ExamPage user={user} onLogout={handleLogout} /> : <Navigate to="/login" />}
        />

        {/* Admin Dashboard */}
        <Route
          path="/admin"
          element={user ? <AdminDashboard user={user} /> : <Navigate to="/login" />}
        />

        {/* Default Redirect */}
        <Route
          path="/"
          element={<Navigate to={user ? "/exam" : "/login"} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
