import React, { useState, useEffect, useRef } from "react";
import * as faceapi from "face-api.js";
import { authApi } from "../api";
import "./RegisterPage.css";

export default function RegistrationFlow({ onRegistered }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student");
  const [secretKey, setSecretKey] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const [registeredUser, setRegisteredUser] = useState(null);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [captureStatus, setCaptureStatus] = useState("idle"); // idle, capturing, captured

  const videoRef = useRef();
  const canvasRef = useRef();
  const captureInterval = useRef(null);
  const descriptorsRef = useRef([]);

  // Load face-api models
  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = "/models";
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);
      setModelsLoaded(true);
    };
    loadModels();
  }, []);

  // Start camera
  const startCamera = async () => {
    try {
      if (videoRef.current && !cameraStarted) {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
        setCameraStarted(true);
      }
    } catch (err) {
      alert("Cannot access camera. Please allow webcam permissions.");
      console.error(err);
    }
  };

  // Register user
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      const res = await authApi.register({ name, email, password, role, secretKey });
      setRegisteredUser(res.data.user);
      setMessage(res.data.message);
      if (onRegistered) onRegistered();

      // Start camera automatically
      await startCamera();
    } catch (err) {
      setMessage(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  // Start face capture
  const startCapture = () => {
    if (!registeredUser || !cameraStarted) return;
    setCaptureStatus("capturing");
    descriptorsRef.current = [];

    captureInterval.current = setInterval(async () => {
      if (!videoRef.current) return;

      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (detection) {
        descriptorsRef.current.push(Array.from(detection.descriptor));

        // Draw detection on canvas
        if (canvasRef.current) {
          const dims = faceapi.matchDimensions(canvasRef.current, videoRef.current, true);
          const resized = faceapi.resizeResults(detection, dims);
          const ctx = canvasRef.current.getContext("2d");
          ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
          faceapi.draw.drawDetections(canvasRef.current, resized);
          faceapi.draw.drawFaceLandmarks(canvasRef.current, resized);
        }

        if (descriptorsRef.current.length >= 5) {
          clearInterval(captureInterval.current);
          setCaptureStatus("captured");
        }
      }
    }, 500);
  };

  // Save face to backend
  const saveFace = async () => {
    if (!registeredUser || descriptorsRef.current.length === 0) return;

    const avgDescriptor = descriptorsRef.current[0].map((_, i) =>
      descriptorsRef.current.reduce((sum, desc) => sum + desc[i], 0) / descriptorsRef.current.length
    );

    try {
      await fetch("http://localhost:4000/api/user/save-face", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: registeredUser._id, descriptor: avgDescriptor }),
      });
      alert("âœ… Face registered successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to save face. Try again.");
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        {!registeredUser ? (
          <>
            <h2 className="register-header">Register</h2>
            <form className="register-form" onSubmit={handleRegister}>
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                className="register-input"
              />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="register-input"
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="register-input"
              />
              <select value={role} onChange={(e) => setRole(e.target.value)} className="register-input">
                <option value="student">Student</option>
                <option value="admin">Admin</option>
              </select>
              {role === "admin" && (
                <input
                  type="password"
                  placeholder="Admin Secret Key"
                  value={secretKey}
                  onChange={(e) => setSecretKey(e.target.value)}
                  required
                  className="register-input"
                />
              )}
              {message && <div className="register-error">{message}</div>}
              <button type="submit" className="register-btn" disabled={loading}>
                {loading ? "Registering..." : "Register"}
              </button>
            </form>
          </>
        ) : (
          <>
            <h2 className="register-header">ðŸŽ¥ Face Registration</h2>
            <div className="camera-container">
              <video ref={videoRef} autoPlay width="320" height="240" className="face-video" />
              <canvas ref={canvasRef} className="detection-canvas" width="320" height="240" />
            </div>
            <div className="face-btns">
              {captureStatus === "idle" && (
                <button onClick={startCapture} disabled={!modelsLoaded}>
                  Start Capture
                </button>
              )}
              {captureStatus === "capturing" && <p>Capturing face samples...</p>}
              {captureStatus === "captured" && (
                <button onClick={saveFace}>Save Face</button>
              )}
            </div>
            {!modelsLoaded && <p>Loading face detection models...</p>}
          </>
        )}
      </div>
    </div>
  );
}
