import React, { useState } from "react";
import { authApi } from "../api";
import FaceRegistration from "./FaceRegistration";
import "./RegisterPage.css";

export default function RegisterPage({ onRegistered }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student"); 
  const [secretKey, setSecretKey] = useState(""); 
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const [registeredUser, setRegisteredUser] = useState(null);
  const [faceRegistered, setFaceRegistered] = useState(false);

  // Register user
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const res = await authApi.register({ name, email, password, role, secretKey });
      setRegisteredUser(res.data.user);
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      {!registeredUser ? (
        <div className="register-box">
          <h2 className="register-header">Register</h2>
          <form className="register-form" onSubmit={handleRegister}>
            <input
              className="register-input"
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <input
              className="register-input"
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              className="register-input"
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <select
              className="register-input"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="student">Student</option>
              <option value="admin">Admin</option>
            </select>
            {role === "admin" && (
              <input
                className="register-input"
                type="password"
                placeholder="Admin Secret Key"
                value={secretKey}
                onChange={(e) => setSecretKey(e.target.value)}
                required
              />
            )}

            {message && <div className="register-error">{message}</div>}

            <button className="register-btn" disabled={loading}>
              {loading ? "Registering..." : "Register"}
            </button>
          </form>
        </div>
      ) : (
        !faceRegistered && (
          <FaceRegistration
            user={registeredUser}
            onComplete={() => setFaceRegistered(true)}
          />
        )
      )}

      {faceRegistered && (
        <div className="success-message">
          <h3>🎉 Registration Complete!</h3>
          <p>You can now log in and take exams.</p>
        </div>
      )}
    </div>
  );
}
