import React, { useState, useRef, useEffect } from "react";
import { authApi } from "../api";
import * as faceapi from "face-api.js";
import "./RegisterPage.css";

export default function RegisterPage({ onRegistered }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student"); // default role
  const [secretKey, setSecretKey] = useState(""); // for admin
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const [registeredUser, setRegisteredUser] = useState(null);
  const [modelsLoaded, setModelsLoaded] = useState(false);

  const videoRef = useRef();

  // Load face-api models
  useEffect(() => {
    const loadModels = async () => {
      const MODEL_URL = "/models"; // folder containing face-api models
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);
      setModelsLoaded(true);
    };
    loadModels();
  }, []);

  // Start webcam
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (err) {
      alert("Cannot access camera. Please allow webcam permissions.");
      console.error(err);
    }
  };

  // Capture face descriptor
  const captureFace = async () => {
    if (!registeredUser) {
      alert("Register first to capture your face!");
      return;
    }

    const detection = await faceapi
      .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
      .withFaceLandmarks()
      .withFaceDescriptor();

    if (!detection) {
      alert("No face detected! Please ensure your face is visible to the camera.");
      return;
    }

    const descriptor = Array.from(detection.descriptor);

    try {
      await fetch("http://localhost:4000/api/user/save-face", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: registeredUser._id, descriptor }),
      });
      alert("âœ… Face registered successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to save face data. Try again.");
    }
  };

  // Register user
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    try {
      const res = await authApi.register({ name, email, password, role, secretKey });
      setMessage(res.data.message);
      setRegisteredUser(res.data.user);
      if (onRegistered) onRegistered();
    } catch (err) {
      setMessage(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2 className="register-header">Register</h2>
        <form className="register-form" onSubmit={handleRegister}>
          <input
            className="register-input"
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            className="register-input"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            className="register-input"
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <select
            className="register-input"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="admin">Admin</option>
          </select>

          {role === "admin" && (
            <input
              className="register-input"
              type="password"
              placeholder="Admin Secret Key"
              value={secretKey}
              onChange={(e) => setSecretKey(e.target.value)}
              required
            />
          )}

          {message && <div className="register-error">{message}</div>}

          <button className="register-btn" disabled={loading}>
            {loading ? "Registering..." : "Register"}
          </button>
        </form>

        {/* Face Registration Section */}
        <div className="face-registration">
          <h3>ðŸŽ¥ Face Registration</h3>
          <video ref={videoRef} autoPlay width="320" height="240" className="face-video" />
          <div className="face-btns">
            <button type="button" onClick={startCamera}>
              Start Camera
            </button>
            <button
              type="button"
              onClick={captureFace}
              disabled={!modelsLoaded || !registeredUser}
            >
              Capture Face
            </button>
          </div>
          {!modelsLoaded && <p>Loading face detection models...</p>}
        </div>
      </div>
    </div>
  );
}
