import React, { useEffect, useState } from "react";

export default function SubmissionPage({ user, examId, onBack }) {
  const [submission, setSubmission] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSubmission = async () => {
      try {
        const res = await fetch(`/api/exam/submission/${user._id}/${examId}`, {
          headers: {
            "Authorization": "Bearer " + localStorage.getItem("token"),
          },
        });
        const data = await res.json();
        setSubmission(data);
      } catch (err) {
        console.error("Error fetching submission:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchSubmission();
  }, [user, examId]);

  if (loading) return <p>Loading submission...</p>;
  if (!submission) return <p>No submission found.</p>;

  const correctCount = submission.answers.filter(a => a.correct).length;

  return (
    <div className="container">
      <h2>Exam Submission Details</h2>
      <p><strong>Username:</strong> {submission.username}</p>
      <p><strong>Exam ID:</strong> {submission.examId}</p>
      <p><strong>Score:</strong> {correctCount} / {submission.answers.length}</p>

      <h3>Tab Switches ({submission.tabSwitches.length})</h3>
      <ul>
        {submission.tabSwitches.map((t, i) => (
          <li key={i}>{new Date(t.timestamp).toLocaleTimeString()}</li>
        ))}
      </ul>

      <h3>Multiple Face Detections ({submission.multipleFaceLogs.length})</h3>
      <ul>
        {submission.multipleFaceLogs.map((log, i) => (
          <li key={i}>
            {new Date(log.startTime).toLocaleTimeString()} - {new Date(log.endTime).toLocaleTimeString()} ({log.duration}s)
          </li>
        ))}
      </ul>

      <h3>Answers</h3>
      <ul>
        {submission.answers.map((a, i) => (
          <li key={i}>
            Question ID: {a.questionId}, Selected: {a.selectedOption}, Correct: {a.correct ? "✅" : "❌"}
          </li>
        ))}
      </ul>

      <button onClick={onBack} style={{ marginTop: 20 }}>Back to Dashboard</button>
    </div>
  );
}
