import React, { useState, useEffect } from 'react';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import ExamPage from './components/ExamPage';
import './styles.css';
import { setAuthToken } from './api';

function App() {
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);

  // Load token from localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const name = localStorage.getItem('name');
    const userId = localStorage.getItem('userId');
    if (token && name && userId) {
  setAuthToken(token); // this sets axios Authorization header
  setUser({ name, _id: userId });
}
  }, []);

  // Handle login
  const handleLogin = (token, name, userId) => {
    localStorage.setItem('token', token);
    localStorage.setItem('name', name);
    localStorage.setItem('userId', userId);
    setAuthToken(token);
    setUser({ name, userId });
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('name');
    localStorage.removeItem('userId');
    setAuthToken(null);
    setUser(null);
    setShowRegister(false);
  };

  // Show exam page if logged in
  if (user) {
    return <ExamPage user={user} onLogout={handleLogout} />;
  }

  // Show login/register forms if not logged in
  return (
    <div className="app-container">
      <div className={`form-container ${showRegister ? 'fade-in' : 'fade-out'}`}>
        {showRegister ? (
          <RegisterPage
            onRegistered={(token, name, userId) => handleLogin(token, name, userId)}
          />
        ) : (
          <LoginPage
            onLogin={handleLogin}
            onShowRegister={() => setShowRegister(true)}
          />
        )}
      </div>
    </div>
  );
}

export default App;
