// =============================================
// ExamPage.js - Complete with All Features
// Features: Face Verification, Mobile Detection, Tab Switching, Multiple Face Detection
// =============================================

import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import * as cocoSsd from '@tensorflow-models/coco-ssd';
import '@tensorflow/tfjs';
import { useNavigate, useParams } from "react-router-dom";
import "./ExamPage.css";


export default function ExamPage({ user, onLogout }) {
  const { examId } = useParams();
  const videoRef = useRef(null);
  const navigate = useNavigate();

  // Basic exam states
  const [paper, setPaper] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [currentQ, setCurrentQ] = useState(0);
  const [timeLeft, setTimeLeft] = useState(600);
  const [logs, setLogs] = useState([]);
  const isSubmitting = useRef(false);

  // Tab switching states
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);

  // Face verification states
  const [registeredDescriptor, setRegisteredDescriptor] = useState(null);
  const [faceVerificationStatus, setFaceVerificationStatus] = useState("loading");
  const [verificationFailures, setVerificationFailures] = useState(0);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const verificationInterval = useRef(null);

  // Multiple face detection states
  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);

  // Mobile phone detection states
  const [mobileModel, setMobileModel] = useState(null);
  const [mobileDetectionCount, setMobileDetectionCount] = useState(0);
  const [showMobileWarning, setShowMobileWarning] = useState(false);
  const [mobileWarningMessage, setMobileWarningMessage] = useState('');
  const mobileDetectionInterval = useRef(null);
  const mobileWarningCount = useRef(0);

  // =============================================
  // INITIALIZATION - Load Everything
  // =============================================

  useEffect(() => {
    loadRegisteredFace();
    loadModelsAndExam();
    loadMobileDetectionModel();

    return () => {
      // Cleanup on unmount
      if (verificationInterval.current) clearInterval(verificationInterval.current);
      if (mobileDetectionInterval.current) clearInterval(mobileDetectionInterval.current);
    };
  }, [examId]);

  // =============================================
  // 1. LOAD REGISTERED FACE DESCRIPTOR
  // =============================================

  const loadRegisteredFace = async () => {
    try {
      console.log("üîÑ Loading registered face descriptor...");
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:4000/api/auth/face-descriptor", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        console.log("‚úÖ Face descriptor loaded, length:", data.faceDescriptor?.length);
        
        if (data.faceDescriptor && Array.isArray(data.faceDescriptor) && data.faceDescriptor.length === 128) {
          setRegisteredDescriptor(new Float32Array(data.faceDescriptor));
          setFaceVerificationStatus("ready");
        } else {
          throw new Error("Invalid face descriptor");
        }
      } else {
        alert("‚ö†Ô∏è Please register your face before taking exams!");
        navigate("/face-registration");
      }
    } catch (error) {
      console.error("‚ùå Error loading face descriptor:", error);
      alert("Failed to load face verification. Please register your face.");
      navigate("/face-registration");
    }
  };

  // =============================================
  // 2. LOAD MODELS AND EXAM
  // =============================================

  const loadModelsAndExam = async () => {
    try {
      console.log("üîÑ Loading face detection models...");
      const MODEL_URL = process.env.PUBLIC_URL + "/models";
      
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);
      
      console.log("‚úÖ Face detection models loaded!");
      setModelsLoaded(true);

      // Load exam paper
      const res = await fetch(`http://localhost:4000/api/exam/paper/${examId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });

      if (!res.ok) throw new Error(`Server error: ${res.status}`);
      const data = await res.json();

      if (!data?.questions?.length) throw new Error("No questions in exam");

      setPaper(data);
      setAnswers(new Array(data.questions.length).fill(null));
      setTimeLeft((data.durationMins || 10) * 60);

      // Start camera
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 640, height: 480 } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        console.log("‚úÖ Camera started");
      }
    } catch (err) {
      console.error("‚ùå Setup failed:", err);
      alert(`Failed to load exam: ${err.message}`);
      navigate("/student/dashboard");
    }
  };

  // =============================================
  // 3. LOAD MOBILE DETECTION MODEL
  // =============================================

  const loadMobileDetectionModel = async () => {
    try {
      console.log("üì± Loading mobile detection model...");
      const loadedModel = await cocoSsd.load();
      setMobileModel(loadedModel);
      console.log("‚úÖ Mobile detection model loaded!");
    } catch (error) {
      console.error("‚ùå Failed to load mobile detection:", error);
    }
  };

  // =============================================
  // 4. FACE VERIFICATION - Continuous Monitoring
  // =============================================

  useEffect(() => {
    if (!paper || !registeredDescriptor || !modelsLoaded) {
      console.log("‚è≥ Waiting for initialization...");
      return;
    }

    console.log("üöÄ Starting face verification...");

    verificationInterval.current = setInterval(async () => {
      await verifyFaceIdentity();
    }, 5000); // Every 5 seconds

    return () => {
      if (verificationInterval.current) {
        clearInterval(verificationInterval.current);
      }
    };
  }, [paper, registeredDescriptor, modelsLoaded]);

  const verifyFaceIdentity = async () => {
    if (!videoRef.current || !registeredDescriptor) return;

    try {
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions({ inputSize: 224 }))
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        console.warn("‚ö†Ô∏è No face detected");
        await logVerification("no_face", 0, "No face in frame");
        setLogs((prev) => [...prev, `‚ö†Ô∏è No face at ${new Date().toLocaleTimeString()}`]);
        return;
      }

      const distance = faceapi.euclideanDistance(registeredDescriptor, detection.descriptor);
      const threshold = 0.6;
      const isMatch = distance < threshold;
      const confidence = Math.max(0, 1 - distance);

      console.log(`üîç Verification: ${isMatch ? 'MATCH' : 'MISMATCH'} (distance: ${distance.toFixed(3)})`);

      if (isMatch) {
        await logVerification("verified", confidence, `Verified (d=${distance.toFixed(3)})`);
        setFaceVerificationStatus("verified");
        setVerificationFailures(0); // Reset on success
      } else {
        const newCount = verificationFailures + 1;
        setVerificationFailures(newCount);
        
        await logVerification("failed", confidence, `Mismatch (d=${distance.toFixed(3)})`);
        
        setLogs((prev) => [
          ...prev,
          `üö® Identity mismatch #${newCount} at ${new Date().toLocaleTimeString()}`
        ]);

        alert(`üö® IDENTITY MISMATCH!\n\nAttempt ${newCount} of 3\nDistance: ${distance.toFixed(3)}\n\nEnsure you are the registered user!`);

        if (newCount >= 3) {
          alert("üö® 3 identity verification failures! Exam will be auto-submitted.");
          handleSubmit();
        }
      }
    } catch (error) {
      console.error("‚ùå Verification error:", error);
    }
  };

  const logVerification = async (status, confidence, details) => {
    try {
      await fetch("http://localhost:4000/api/exam/verify-face", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          examId,
          verificationStatus: status,
          confidence,
          details,
        }),
      });
    } catch (error) {
      console.error("‚ùå Log error:", error);
    }
  };

  // =============================================
  // 5. MOBILE PHONE DETECTION
  // =============================================

  useEffect(() => {
    if (!mobileModel || !videoRef.current || !paper) return;

    console.log("üì± Starting mobile phone detection...");

    mobileDetectionInterval.current = setInterval(async () => {
      await detectMobilePhone();
    }, 2000); // Every 2 seconds

    return () => {
      if (mobileDetectionInterval.current) {
        clearInterval(mobileDetectionInterval.current);
      }
    };
  }, [mobileModel, paper]);

  const detectMobilePhone = async () => {
    if (!videoRef.current || videoRef.current.readyState !== 4) return;

    try {
      const predictions = await mobileModel.detect(videoRef.current);
      
      const phoneDetections = predictions.filter(prediction => 
        (prediction.class === 'cell phone' || prediction.class === 'phone') &&
        prediction.score > 0.5
      );

      if (phoneDetections.length > 0) {
        const detection = phoneDetections[0];
        const confidence = (detection.score * 100).toFixed(1);

        console.warn(`üì± Mobile phone detected! Confidence: ${confidence}%`);
        
        mobileWarningCount.current += 1;
        setMobileDetectionCount(prev => prev + 1);

        // Log to backend
        await logMobileDetection('phone_detected', detection.score, mobileWarningCount.current);

        // Show warning
        const message = `Mobile phone detected!\nWarning ${mobileWarningCount.current}/3\nConfidence: ${confidence}%`;
        setMobileWarningMessage(message);
        setShowMobileWarning(true);
        
        setLogs((prev) => [
          ...prev,
          `üì± Mobile detected at ${new Date().toLocaleTimeString()} - Warning ${mobileWarningCount.current}/3`
        ]);

        setTimeout(() => setShowMobileWarning(false), 5000);

        // Alert for warnings
        if (mobileWarningCount.current <= 2) {
          alert(`‚ö†Ô∏è WARNING: Mobile phone detected!\n\nThis is warning ${mobileWarningCount.current} of 3.\n\nPut away your phone or exam will be terminated!`);
        } else if (mobileWarningCount.current >= 3) {
          alert(`üö® CRITICAL: Mobile phone detected ${mobileWarningCount.current} times!\n\nExam will be terminated immediately!`);
          handleSubmit();
        }
      }
    } catch (error) {
      console.error("üì± Mobile detection error:", error);
    }
  };

  const logMobileDetection = async (type, confidence, count) => {
    try {
      await fetch("http://localhost:4000/api/exam/log-mobile-detection", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          examId,
          detectionType: type,
          confidence,
          details: `Phone detected (Warning ${count}/3)`,
          actionTaken: count < 3 ? 'warning' : 'exam_terminated'
        }),
      });
    } catch (error) {
      console.error("Error logging mobile detection:", error);
    }
  };

  // =============================================
  // 6. MULTIPLE FACE DETECTION
  // =============================================

  useEffect(() => {
    if (!paper) return;

    const interval = setInterval(async () => {
      if (!videoRef.current) return;
      
      try {
        const detections = await faceapi.detectAllFaces(
          videoRef.current, 
          new faceapi.TinyFaceDetectorOptions()
        );

        if (detections.length > 1) {
          if (!multipleFaceActive.current) {
            multipleFaceActive.current = true;
            startTimeRef.current = new Date();
            setLogs((prev) => [
              ...prev,
              `üë• ${detections.length} faces detected at ${startTimeRef.current.toLocaleTimeString()}`
            ]);
          }

          await fetch("http://localhost:4000/api/exam/proctor-event", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
            body: JSON.stringify({
              examId: examId,
              type: "multiple-face",
              data: { message: `Detected ${detections.length} faces` },
            }),
          });
        } else if (multipleFaceActive.current) {
          multipleFaceActive.current = false;
          const endTime = new Date();
          const duration = Math.round((endTime - startTimeRef.current) / 1000);
          setLogs((prev) => [
            ...prev,
            `Multiple faces ended at ${endTime.toLocaleTimeString()} (${duration}s)`
          ]);
        }
      } catch (error) {
        console.error("Face detection error:", error);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [paper, examId]);

  // =============================================
  // 7. TAB SWITCHING DETECTION
  // =============================================

  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) {
        setTabSwitchCount((prev) => prev + 1);
        setLogs((prev) => [
          ...prev,
          `üîÑ Tab switch detected at ${new Date().toLocaleTimeString()}`
        ]);
      }
    };
    
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("‚ö†Ô∏è Warning: You switched tabs! One more switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) {
      alert("‚ùå 3 tab switches detected. Exam will be auto-submitted.");
      handleSubmit();
    }
  }, [tabSwitchCount, warningShown]);

  // =============================================
  // 8. TIMER COUNTDOWN
  // =============================================

  useEffect(() => {
    if (timeLeft <= 0) {
      alert("‚è∞ Time's up! Exam will be auto-submitted.");
      handleSubmit();
      return;
    }
    
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  // =============================================
  // 9. UTILITY FUNCTIONS
  // =============================================

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${min}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    if (isSubmitting.current) return;
    if (!paper) { 
      alert("Exam data not loaded!"); 
      return; 
    }

    if (tabSwitchCount < 3 && timeLeft > 0 && verificationFailures < 3 && mobileWarningCount.current < 3) {
      if (!window.confirm("Are you sure you want to submit your exam?")) return;
    }

    isSubmitting.current = true;

    try {
      const response = await fetch("http://localhost:4000/api/exam/submit", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({
          userId: user._id,
          examId: examId,
          answers: answers,
        }),
      });

      if (!response.ok) throw new Error(`Submit failed: ${response.status}`);
      const data = await response.json();

      if (data.submissionId) {
        navigate(`/result/${data.submissionId}`);
      } else {
        throw new Error("No submission ID returned");
      }
    } catch (error) {
      console.error("Submit error:", error);
      alert("‚ùå Failed to submit exam. Please try again.");
      isSubmitting.current = false;
    }
  };

  // =============================================
  // 10. RENDER
  // =============================================

  if (!paper || !paper.questions) {
    return (
      <div className="exam-loading">
        <div className="loading-spinner"></div>
        <h3>Loading exam...</h3>
        <p>Please wait while we prepare your exam.</p>
      </div>
    );
  }

  const question = paper.questions[currentQ];

  return (
    <div className="exam-page">
      {/* Mobile Warning Overlay */}
      {showMobileWarning && (
        <div className="mobile-warning-overlay">
          <div className="mobile-warning-content">
            <div className="mobile-warning-icon">üì±</div>
            <h3>Mobile Phone Detected!</h3>
            <p style={{ whiteSpace: 'pre-line' }}>{mobileWarningMessage}</p>
            <p className="mobile-warning-action">Put away your phone immediately!</p>
            <button 
              onClick={() => setShowMobileWarning(false)}
              className="mobile-warning-dismiss"
            >
              I Understand
            </button>
          </div>
        </div>
      )}

      {/* Back Button */}
      <button 
        onClick={() => {
          if (window.confirm("Are you sure you want to exit? Your progress will be lost!")) {
            navigate("/student/dashboard");
          }
        }}
        className="exam-back-btn"
      >
        ‚Üê Back to Dashboard
      </button>

      {/* Verification Status Banner */}
      <div className={`verification-banner ${faceVerificationStatus}`}>
        {faceVerificationStatus === "verified" && "‚úì Identity Verified"}
        {faceVerificationStatus === "loading" && "üîÑ Verifying identity..."}
        {faceVerificationStatus === "ready" && "üë§ Monitoring active"}
        {verificationFailures > 0 && (
          <span className="failure-count">
            ‚ö†Ô∏è Verification failures: {verificationFailures}/3
          </span>
        )}
      </div>

      {/* Detection Counters */}
      <div className="detection-counters">
        <span className="counter-item">
          ‚è±Ô∏è Time: {formatTime(timeLeft)}
        </span>
        <span className="counter-item">
          üîÑ Tab Switches: {tabSwitchCount}/3
        </span>
        <span className="counter-item mobile-counter">
          üì± Mobile Detections: {mobileDetectionCount}/3
        </span>
        <span className="counter-item">
          üîç Identity Checks: {verificationFailures > 0 ? `${verificationFailures}/3 Failed` : '‚úì Passing'}
        </span>
      </div>

      <div className="exam-body">
        <div className="exam-main-content">
          {/* Progress */}
          <div className="exam-progress">
            Question {currentQ + 1} of {paper.questions.length}
          </div>

          {/* Question */}
          <div className="exam-question">
            <h3>Q{currentQ + 1}: {question.text}</h3>
            <div className="exam-options">
              {question.options.map((opt, idx) => (
                <label 
                  key={idx} 
                  className={`exam-option-label ${answers[currentQ] === idx ? 'selected' : ''}`}
                >
                  <input
                    type="radio"
                    name={`q${currentQ}`}
                    checked={answers[currentQ] === idx}
                    onChange={() => handleAnswerChange(currentQ, idx)}
                  />
                  {opt}
                </label>
              ))}
            </div>
          </div>

          {/* Navigation */}
          <div className="exam-nav-buttons">
            {currentQ > 0 && (
              <button 
                onClick={() => setCurrentQ(currentQ - 1)} 
                className="exam-prev-btn"
              >
                ‚¨ÖÔ∏è Previous
              </button>
            )}
            {currentQ < paper.questions.length - 1 ? (
              <button 
                onClick={() => setCurrentQ(currentQ + 1)} 
                className="exam-next-btn"
              >
                Next ‚û°Ô∏è
              </button>
            ) : (
              <button 
                onClick={handleSubmit} 
                className="exam-submit-btn" 
                disabled={isSubmitting.current}
              >
                {isSubmitting.current ? '‚è≥ Submitting...' : 'üöÄ Submit Exam'}
              </button>
            )}
          </div>
        </div>

        {/* Sidebar */}
        <div className="exam-sidebar">
          <div className="exam-camera-preview">
            <h4>üì∑ Monitoring System</h4>
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              playsInline
              style={{ width: "100%", maxWidth: "300px", borderRadius: "8px" }} 
            />
            <div className="camera-status-badges">
              <span className="status-badge camera">üé• Camera</span>
              <span className="status-badge face">üë§ Face ID</span>
              <span className="status-badge mobile">üì± Mobile</span>
            </div>
          </div>

          <div className="exam-face-logs">
            <h4>üìã Activity Logs</h4>
            {logs.length > 0 ? (
              <ul>
                {logs.slice(-10).map((log, i) => (
                  <li 
                    key={i} 
                    className={
                      log.includes('üì±') ? 'mobile-log' :
                      log.includes('üö®') ? 'critical-log' :
                      log.includes('üë•') ? 'multiple-face-log' :
                      ''
                    }
                  >
                    {log}
                  </li>
                ))}
              </ul>
            ) : (
              <p className="exam-face-logs-empty">‚úÖ No issues detected</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
