import React, { useEffect, useRef, useState } from "react";
import * as faceapi from "face-api.js";
import { useNavigate } from "react-router-dom";
import "./PreExamSetup.css";

export default function PreExamSetup({ user, examId, examDetails, onProceed, onCancel }) {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [registeredDescriptor, setRegisteredDescriptor] = useState(null);
  
  // System checks
  const [checks, setChecks] = useState({
    camera: { status: 'pending', message: 'Checking camera access...' },
    models: { status: 'pending', message: 'Loading face detection models...' },
    faceDetection: { status: 'pending', message: 'Waiting for face detection...' },
    lighting: { status: 'pending', message: 'Checking lighting conditions...' },
    identity: { status: 'pending', message: 'Verifying your identity...' },
    singlePerson: { status: 'pending', message: 'Checking for single person...' }
  });

  const [canProceed, setCanProceed] = useState(false);
  const [isChecking, setIsChecking] = useState(true);
  const [verificationAttempts, setVerificationAttempts] = useState(0);
  const [successfulVerifications, setSuccessfulVerifications] = useState(0);
  const [currentIssues, setCurrentIssues] = useState([]);
  const [isManualTesting, setIsManualTesting] = useState(false);
  const detectionInterval = useRef(null);
  const modelsLoaded = useRef(false);
  const verificationComplete = useRef(false); // NEW: Track if verification is done

  useEffect(() => {
    startSystemCheck();
    return () => {
      stopCamera();
      if (detectionInterval.current) clearInterval(detectionInterval.current);
    };
  }, []);

  // Watch for successful verifications reaching 3
  useEffect(() => {
    console.log("üìä Verification count changed:", successfulVerifications);
    
    if (successfulVerifications >= 3 && !verificationComplete.current) {
      console.log("üéâ Reached 3 verifications! Enabling button...");
      verificationComplete.current = true; // Mark as complete
      setCanProceed(true);
      setIsChecking(false);
      
      // Stop continuous checking
      if (detectionInterval.current) {
        console.log("‚è∏Ô∏è Stopping detection interval");
        clearInterval(detectionInterval.current);
        detectionInterval.current = null;
      }
    }
  }, [successfulVerifications]);

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  const startSystemCheck = async () => {
    console.log("üöÄ Starting system check...");
    
    // Step 1: Check Camera Access
    const cameraOk = await checkCamera();
    if (!cameraOk) return;
    
    // Step 2: Load Models
    const modelsOk = await loadModels();
    if (!modelsOk) return;
    
    // Step 3: Load Registered Face
    const faceOk = await loadRegisteredFace();
    if (!faceOk) return;
    
    // Step 4: Wait for video to be fully ready
    await waitForVideo();
    
    // Step 5: Start Continuous Checks
    console.log("‚úÖ Starting continuous face detection...");
    startContinuousChecks();
  };

  const waitForVideo = () => {
    return new Promise((resolve) => {
      const checkVideo = () => {
        if (videoRef.current && videoRef.current.readyState === 4) {
          console.log("‚úÖ Video is ready");
          resolve();
        } else {
          console.log("‚è≥ Waiting for video...");
          setTimeout(checkVideo, 100);
        }
      };
      checkVideo();
    });
  };

  const checkCamera = async () => {
    try {
      console.log("üì∑ Requesting camera access...");
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        
        // Wait for video to be ready
        await new Promise((resolve) => {
          videoRef.current.onloadedmetadata = () => {
            videoRef.current.play();
            console.log("‚úÖ Camera stream started");
            resolve();
          };
        });

        updateCheck('camera', 'success', '‚úì Camera connected');
        return true;
      }
      return false;
    } catch (error) {
      console.error("‚ùå Camera error:", error);
      updateCheck('camera', 'error', '‚úó Camera access denied. Please enable camera.');
      return false;
    }
  };

  const loadModels = async () => {
    try {
      console.log("üîÑ Loading face detection models...");
      const MODEL_URL = process.env.PUBLIC_URL + "/models";
      
      console.log("üìÇ Model path:", MODEL_URL);
      
      // Check if models folder exists by trying to load
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);

      modelsLoaded.current = true;
      console.log("‚úÖ Models loaded successfully");
      
      updateCheck('models', 'success', '‚úì Detection models loaded');
      return true;
    } catch (error) {
      console.error("‚ùå Models error:", error);
      updateCheck('models', 'error', '‚úó Failed to load detection models');
      alert("‚ö†Ô∏è Failed to load face detection models. Please ensure the models are in /public/models folder.");
      return false;
    }
  };

  const loadRegisteredFace = async () => {
    try {
      console.log("üîÑ Loading registered face...");
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:4000/api/auth/face-descriptor", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.faceDescriptor && data.faceDescriptor.length === 128) {
          setRegisteredDescriptor(new Float32Array(data.faceDescriptor));
          console.log("‚úÖ Registered face loaded successfully");
          return true;
        } else {
          throw new Error("Invalid face descriptor");
        }
      } else {
        throw new Error("Face not registered");
      }
    } catch (error) {
      console.error("‚ùå Face registration error:", error);
      updateCheck('identity', 'error', '‚úó Face not registered. Please register first.');
      alert("‚ö†Ô∏è You need to register your face first before taking exams!");
      return false;
    }
  };

  const startContinuousChecks = () => {
    console.log("üîÑ Starting continuous checks...");
    
    // Do first check after a short delay
    setTimeout(() => {
      console.log("‚è∞ Running first check...");
      performAllChecks();
    }, 1000);
    
    // Then continue every 1.5 seconds
    detectionInterval.current = setInterval(async () => {
      if (!verificationComplete.current) { // Only check if not complete
        await performAllChecks();
      }
    }, 1500);
  };

  const performAllChecks = async () => {
    // Stop if already verified
    if (verificationComplete.current) {
      console.log("‚úÖ Already verified, skipping check");
      return;
    }

    // Safety checks
    if (!videoRef.current) {
      console.log("‚ö†Ô∏è Video ref not available");
      return;
    }
    
    const readyState = videoRef.current.readyState;
    
    if (readyState < 3) {
      console.log("‚ö†Ô∏è Video not ready yet, waiting...");
      return;
    }
    
    if (!registeredDescriptor) {
      console.log("‚ö†Ô∏è No registered descriptor");
      return;
    }

    if (!modelsLoaded.current) {
      console.log("‚ö†Ô∏è Models not loaded yet");
      return;
    }

    const issues = [];

    try {
      console.log("üîç Starting face detection...");
      
      // Check if video dimensions are valid
      if (videoRef.current.videoWidth === 0 || videoRef.current.videoHeight === 0) {
        console.error("‚ùå Video has invalid dimensions (0x0)");
        return;
      }
      
      // Detect all faces with timeout
      const detectionPromise = faceapi
        .detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions({ 
          inputSize: 224,
          scoreThreshold: 0.5 
        }))
        .withFaceLandmarks()
        .withFaceDescriptors();
      
      // Add 10 second timeout
      const timeoutPromise = new Promise((_, reject) => 
        setTimeout(() => reject(new Error('Detection timeout')), 10000)
      );
      
      const detections = await Promise.race([detectionPromise, timeoutPromise]);

      console.log(`üìä Detection complete! Found ${detections.length} face(s)`);

      // Draw on canvas
      if (canvasRef.current && videoRef.current) {
        const dims = faceapi.matchDimensions(canvasRef.current, videoRef.current, true);
        const resizedDetections = faceapi.resizeResults(detections, dims);
        
        const ctx = canvasRef.current.getContext('2d');
        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        
        faceapi.draw.drawDetections(canvasRef.current, resizedDetections);
        faceapi.draw.drawFaceLandmarks(canvasRef.current, resizedDetections);
      }

      // Check 1: Face Detection
      if (detections.length === 0) {
        console.log("‚ö†Ô∏è No face detected");
        updateCheck('faceDetection', 'warning', '‚ö† No face detected');
        updateCheck('identity', 'pending', 'Waiting for face...');
        updateCheck('singlePerson', 'pending', 'Waiting for face...');
        updateCheck('lighting', 'pending', 'Waiting for face...');
        issues.push("Position your face in front of the camera");
        setCanProceed(false);
        setCurrentIssues(issues);
        // Reset on no face
        setSuccessfulVerifications(0);
        return;
      } else {
        console.log("‚úÖ Face detected");
        updateCheck('faceDetection', 'success', '‚úì Face detected');
      }

      // Check 2: Single Person
      if (detections.length > 1) {
        console.log(`‚ö†Ô∏è Multiple faces: ${detections.length}`);
        updateCheck('singlePerson', 'error', `‚úó ${detections.length} people detected`);
        issues.push("Multiple people detected. Only you should be visible.");
        setCanProceed(false);
        setCurrentIssues(issues);
        // Reset on multiple faces
        setSuccessfulVerifications(0);
        return;
      } else {
        console.log("‚úÖ Single person confirmed");
        updateCheck('singlePerson', 'success', '‚úì Single person confirmed');
      }

      const detection = detections[0];

      // Check 3: Lighting Quality
      const lightingScore = assessLighting(detection);
      console.log(`üí° Lighting score: ${lightingScore.toFixed(2)}`);
      
      if (lightingScore < 0.3) {
        updateCheck('lighting', 'warning', '‚ö† Poor lighting - improve lighting');
        issues.push("Lighting is poor. Please improve lighting conditions.");
      } else if (lightingScore < 0.6) {
        updateCheck('lighting', 'warning', '‚ö† Lighting could be better');
      } else {
        updateCheck('lighting', 'success', '‚úì Lighting is good');
      }

      // Check 4: Identity Verification - STRICTER MATCHING
      const distance = faceapi.euclideanDistance(registeredDescriptor, detection.descriptor);
      
      // FIXED: Stricter threshold for better identity verification
      const STRICT_THRESHOLD = 0.45; // Lower = stricter (was 0.6)
      const isMatch = distance < STRICT_THRESHOLD;
      const confidence = Math.max(0, (1 - distance) * 100);

      console.log(`üîê Identity check: distance=${distance.toFixed(3)}, threshold=${STRICT_THRESHOLD}, match=${isMatch}, confidence=${confidence.toFixed(1)}%`);

      setVerificationAttempts(prev => prev + 1);

      if (isMatch && confidence >= 60) { // FIXED: Also require minimum 60% confidence
        const newCount = successfulVerifications + 1;
        console.log(`‚úÖ Successful verification ${newCount}/3 (confidence: ${confidence.toFixed(1)}%)`);
        
        setSuccessfulVerifications(newCount);
        updateCheck('identity', 'success', `‚úì Identity verified (${confidence.toFixed(1)}% match)`);
        
        // Check if we've reached the threshold
        if (newCount >= 3 && issues.length === 0) {
          console.log("üéâ REACHED 3 SUCCESSFUL VERIFICATIONS!");
          verificationComplete.current = true; // Mark as complete
          setCanProceed(true);
          setIsChecking(false);
          setCurrentIssues([]);
          
          // Stop checking
          if (detectionInterval.current) {
            console.log("‚è∏Ô∏è Stopping continuous checks - ready to proceed");
            clearInterval(detectionInterval.current);
            detectionInterval.current = null;
          }
          return;
        }
      } else {
        console.log(`‚ùå Identity mismatch: distance=${distance.toFixed(3)}, confidence=${confidence.toFixed(1)}%`);
        updateCheck('identity', 'error', `‚úó Identity mismatch (${confidence.toFixed(1)}% confidence - need 60%+)`);
        issues.push(`Face doesn't match registered user (${confidence.toFixed(1)}% match). Please ensure you are the registered user.`);
        setSuccessfulVerifications(0); // Reset counter on mismatch
        setCanProceed(false);
        setCurrentIssues(issues);
        return;
      }

      // All checks passed for this iteration
      setCurrentIssues(issues);
      console.log(`‚è≥ Verification progress: ${successfulVerifications + 1}/3 successful`);

    } catch (error) {
      console.error("‚ùå Unexpected error in performAllChecks:", error);
      updateCheck('faceDetection', 'error', '‚úó Detection error - check console');
      setSuccessfulVerifications(0); // Reset on error
    }
  };

  const assessLighting = (detection) => {
    // Simple lighting assessment based on detection confidence
    const score = detection.detection.score;
    return score;
  };

  const updateCheck = (checkName, status, message) => {
    setChecks(prev => ({
      ...prev,
      [checkName]: { status, message }
    }));
  };

  const handleProceed = () => {
    console.log("üöÄ PROCEED BUTTON CLICKED");
    console.log("Can proceed:", canProceed);
    console.log("Successful verifications:", successfulVerifications);
    
    if (canProceed && successfulVerifications >= 3 && verificationComplete.current) {
      console.log("‚úÖ Proceeding to exam...");
      stopCamera();
      onProceed({
        registeredDescriptor,
        preCheckPassed: true
      });
    } else {
      console.log("‚ùå Cannot proceed yet");
      alert(`Please wait for all verifications to complete (${successfulVerifications}/3)`);
    }
  };

  const handleCancel = () => {
    console.log("‚ùå Setup cancelled");
    stopCamera();
    onCancel();
  };

  // FIXED: Manual test button with proper controls
  const handleManualTest = async () => {
    // Prevent multiple clicks if already verified
    if (verificationComplete.current) {
      console.log("‚ö†Ô∏è Already verified, cannot test again");
      alert("‚úÖ You are already verified! Click 'Start Exam' to proceed.");
      return;
    }

    // Prevent clicking while testing
    if (isManualTesting) {
      console.log("‚ö†Ô∏è Manual test already in progress");
      return;
    }

    console.log("üß™ Manual identity verification started...");
    setIsManualTesting(true);
    
    try {
      await performAllChecks();

      if (successfulVerifications >= 3 && verificationComplete.current) {
        console.log("‚úÖ Manual test successful ‚Äî user verified");
        alert("‚úÖ Identity verified successfully! You can now start the exam.");
      } else {
        alert(`‚ö†Ô∏è Verification incomplete. (${successfulVerifications}/3 successful checks)\n\nPlease ensure:\n‚Ä¢ Your face is clearly visible\n‚Ä¢ Good lighting conditions\n‚Ä¢ You are the registered user\n‚Ä¢ You are alone in the frame`);
      }
    } finally {
      setIsManualTesting(false);
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'success': return '‚úì';
      case 'error': return '‚úó';
      case 'warning': return '‚ö†';
      case 'pending': return '‚è≥';
      default: return '‚óã';
    }
  };

  const getStatusClass = (status) => {
    return `check-item status-${status}`;
  };

  return (
    <div className="pre-exam-setup">
      <div className="setup-container">
        <div className="setup-header">
          <h2>üì∏ Pre-Exam System Check</h2>
          <p>Please ensure all checks pass before starting the exam</p>
          {successfulVerifications > 0 && (
            <div style={{ 
              marginTop: '1rem', 
              padding: '0.75rem', 
              background: successfulVerifications >= 3 ? '#d1fae5' : '#fef3c7',
              borderRadius: '8px',
              fontWeight: '600'
            }}>
              {successfulVerifications >= 3 ? (
                <span style={{ color: '#059669' }}>‚úÖ Verification Complete ({successfulVerifications}/3)</span>
              ) : (
                <span style={{ color: '#d97706' }}>‚è≥ Verifying... ({successfulVerifications}/3 successful checks)</span>
              )}
            </div>
          )}
          
          {/* Manual test button - FIXED */}
          <button 
            onClick={handleManualTest}
            style={{
              marginTop: '1rem',
              padding: '0.5rem 1rem',
              background: verificationComplete.current ? '#10b981' : isManualTesting ? '#9ca3af' : '#3b82f6',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: verificationComplete.current || isManualTesting ? 'not-allowed' : 'pointer',
              fontSize: '0.875rem',
              opacity: verificationComplete.current || isManualTesting ? 0.6 : 1
            }}
            disabled={verificationComplete.current || isManualTesting}
          >
            {verificationComplete.current ? '‚úÖ Identity Verified' : 
             isManualTesting ? 'üîÑ Testing...' : 
             'üîç Start Manual Identity Test'}
          </button>
        </div>

        <div className="setup-content">
          {/* Camera Preview */}
          <div className="camera-section">
            <div className="camera-preview">
              <video
                ref={videoRef}
                autoPlay
                muted
                playsInline
                className="preview-video"
                onLoadedMetadata={() => {
                  if (canvasRef.current && videoRef.current) {
                    canvasRef.current.width = videoRef.current.videoWidth;
                    canvasRef.current.height = videoRef.current.videoHeight;
                    console.log("üìê Canvas dimensions set:", canvasRef.current.width, "x", canvasRef.current.height);
                  }
                }}
              />
              <canvas ref={canvasRef} className="preview-canvas" />
            </div>
            
            {currentIssues.length > 0 && (
              <div className="current-issues">
                <h4>‚ö†Ô∏è Current Issues:</h4>
                <ul>
                  {currentIssues.map((issue, index) => (
                    <li key={index}>{issue}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* System Checks */}
          <div className="checks-section">
            <h3>System Checks</h3>
            <div className="checks-list">
              <div className={getStatusClass(checks.camera.status)}>
                <span className="check-icon">{getStatusIcon(checks.camera.status)}</span>
                <span className="check-message">{checks.camera.message}</span>
              </div>

              <div className={getStatusClass(checks.models.status)}>
                <span className="check-icon">{getStatusIcon(checks.models.status)}</span>
                <span className="check-message">{checks.models.message}</span>
              </div>

              <div className={getStatusClass(checks.faceDetection.status)}>
                <span className="check-icon">{getStatusIcon(checks.faceDetection.status)}</span>
                <span className="check-message">{checks.faceDetection.message}</span>
              </div>

              <div className={getStatusClass(checks.singlePerson.status)}>
                <span className="check-icon">{getStatusIcon(checks.singlePerson.status)}</span>
                <span className="check-message">{checks.singlePerson.message}</span>
              </div>

              <div className={getStatusClass(checks.lighting.status)}>
                <span className="check-icon">{getStatusIcon(checks.lighting.status)}</span>
                <span className="check-message">{checks.lighting.message}</span>
              </div>

              <div className={getStatusClass(checks.identity.status)}>
                <span className="check-icon">{getStatusIcon(checks.identity.status)}</span>
                <span className="check-message">{checks.identity.message}</span>
              </div>
            </div>

            {verificationAttempts > 0 && successfulVerifications < 3 && !canProceed && (
              <div className="verification-progress">
                <p>Verifying identity... {successfulVerifications}/3 successful checks needed</p>
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${Math.min((successfulVerifications / 3) * 100, 100)}%` }}
                  ></div>
                </div>
                <p style={{ fontSize: '0.85rem', color: '#666', marginTop: '0.5rem' }}>
                  Keep your face steady... {3 - successfulVerifications} more successful verification(s) needed
                </p>
              </div>
            )}

            {successfulVerifications >= 3 && canProceed && (
              <div className="verification-complete">
                <p>‚úÖ Identity verification complete!</p>
              </div>
            )}
          </div>
        </div>

        {/* Exam Information */}
        {examDetails && (
          <div className="exam-info">
            <h3>Exam Details</h3>
            <div className="exam-details-grid">
              <div className="detail-item">
                <span className="detail-label">Title:</span>
                <span className="detail-value">{examDetails?.title}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Questions:</span>
                <span className="detail-value">{examDetails?.questions?.length || 0}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Duration:</span>
                <span className="detail-value">{examDetails?.durationMins || 30} minutes</span>
              </div>
            </div>
          </div>
        )}

        {/* Instructions */}
        <div className="setup-instructions">
          <h4>üí° Setup Tips:</h4>
          <ul>
            <li>Ensure you're in a well-lit room</li>
            <li>Position your face clearly in front of the camera</li>
            <li>Be alone - no one else should be visible</li>
            <li>Remove glasses or accessories if causing issues</li>
            <li>Keep your face steady during verification</li>
            <li>Close unnecessary browser tabs</li>
            <li>‚ö†Ô∏è Only the registered user can take the exam</li>
          </ul>
        </div>

        {/* Action Buttons */}
        <div className="setup-actions">
          <button onClick={handleCancel} className="btn-cancel">
            Cancel
          </button>
          
          {canProceed && successfulVerifications >= 3 ? (
            <button onClick={handleProceed} className="btn-proceed">
              ‚úì Start Exam ({successfulVerifications}/3 verified)
            </button>
          ) : (
            <button disabled className="btn-proceed-disabled">
              {isChecking ? `‚è≥ Verifying... (${successfulVerifications}/3)` : '‚óã Waiting for verification'}
            </button>
          )}
        </div>

        {canProceed && successfulVerifications >= 3 && (
          <div className="ready-message">
            <p>üéâ All systems ready! Click "Start Exam" to begin.</p>
          </div>
        )}
      </div>
    </div>
  );
}
