import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./ExamCreator.css";

export default function ExamCreator({ user, isEditMode }) {
  const { examId } = useParams();
  const [title, setTitle] = useState("");
  const [durationMins, setDurationMins] = useState(30);
  const [questions, setQuestions] = useState([
    {
      text: "",
      options: ["", "", "", ""],
      correctOption: 0,
      image: null,
      imagePreview: null
    },
  ]);
  const [loading, setLoading] = useState(false);
  const [loadingExam, setLoadingExam] = useState(isEditMode);
  const navigate = useNavigate();

  // Load exam data if in edit mode
  useEffect(() => {
    if (isEditMode && examId) {
      fetchExamData();
    }
  }, [isEditMode, examId]);

  const fetchExamData = async () => {
    try {
      setLoadingExam(true);
      const token = localStorage.getItem("token");
      
      // Use the correct endpoint for fetching a single exam
      const response = await fetch(`http://localhost:4000/api/admin/exam/${examId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const examData = await response.json();
        console.log("Loaded exam data:", examData);
        setTitle(examData.title);
        setDurationMins(examData.durationMins);
        
        // Map questions and add image properties if they don't exist
        const formattedQuestions = examData.questions.map(q => ({
          ...q,
          image: q.image || null,
          imagePreview: q.image ? `http://localhost:4000/${q.image}` : null
        }));
        
        setQuestions(formattedQuestions);
      } else {
        alert("Failed to load exam data");
      }
    } catch (error) {
      console.error("Error loading exam:", error);
      alert("Error loading exam data");
    } finally {
      setLoadingExam(false);
    }
  };

  const handleAddQuestion = () => {
    setQuestions([
      ...questions,
      {
        text: "",
        options: ["", "", "", ""],
        correctOption: 0,
        image: null,
        imagePreview: null
      },
    ]);
  };

  const handleRemoveQuestion = (index) => {
    if (questions.length === 1) {
      alert("You must have at least one question");
      return;
    }
    const newQuestions = questions.filter((_, i) => i !== index);
    setQuestions(newQuestions);
  };

  const handleQuestionChange = (index, field, value) => {
    const newQuestions = [...questions];
    newQuestions[index][field] = value;
    setQuestions(newQuestions);
  };

  const handleImageUpload = (qIndex, e) => {
    const file = e.target.files[0];
    if (!file) return;

    const newQuestions = [...questions];
    newQuestions[qIndex].image = file;
    
    // Create a preview URL for the image
    const reader = new FileReader();
    reader.onloadend = () => {
      newQuestions[qIndex].imagePreview = reader.result;
      setQuestions([...newQuestions]);
    };
    reader.readAsDataURL(file);
  };

  const handleRemoveImage = (qIndex) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].image = null;
    newQuestions[qIndex].imagePreview = null;
    setQuestions([...newQuestions]);
  };

  const handleOptionChange = (qIndex, oIndex, value) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const handleCorrectOptionChange = (qIndex, oIndex) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].correctOption = oIndex;
    setQuestions(newQuestions);
  };

  const validateExam = () => {
    if (!title.trim()) {
      alert("Please enter an exam title");
      return false;
    }

    if (durationMins < 1) {
      alert("Duration must be at least 1 minute");
      return false;
    }

    for (let i = 0; i < questions.length; i++) {
      const q = questions[i];
      
      if (!q.text.trim()) {
        alert(`Please enter text for question ${i + 1}`);
        return false;
      }

      for (let j = 0; j < q.options.length; j++) {
        if (!q.options[j].trim()) {
          alert(`Please fill in option ${j + 1} for question ${i + 1}`);
          return false;
        }
      }
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateExam()) return;

    setLoading(true);

    try {
      const token = localStorage.getItem("token");
      
      // Create FormData to handle file uploads
      const formData = new FormData();
      formData.append("title", title);
      formData.append("durationMins", durationMins);
      
      // Process questions and handle file uploads
      const questionsData = questions.map((q, index) => {
        // Create a copy without the file object (we'll handle files separately)
        const questionCopy = { ...q };
        
        // If there's an image file, append it to formData with question index
        if (q.image instanceof File) {
          formData.append(`questionImage_${index}`, q.image);
          formData.append(`hasNewImage_${index}`, 'true');
        } else if (q.imagePreview && q.imagePreview.startsWith('http')) {
          // Keep existing image path
          formData.append(`existingImage_${index}`, q.image);
        }
        
        // Remove client-side only properties
        delete questionCopy.imagePreview;
        delete questionCopy.image;
        
        return questionCopy;
      });
      
      formData.append("questions", JSON.stringify(questionsData));
      
      // Determine endpoint and method based on mode
      const url = isEditMode 
        ? `http://localhost:4000/api/admin/exam/${examId}` 
        : "http://localhost:4000/api/admin/exam/create";
      
      const method = isEditMode ? "PUT" : "POST";

      // For FormData, don't include Content-Type header as it will be set automatically with boundary
      const response = await fetch(url, {
        method: method,
        body: formData,
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const result = await response.json();
        alert(isEditMode ? "Exam updated successfully!" : "Exam created successfully!");
        navigate("/admin/dashboard");
      } else {
        const error = await response.json();
        alert(`Error: ${error.message || "Something went wrong"}`);
      }
    } catch (error) {
      console.error("Error submitting exam:", error);
      alert("Error submitting exam. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="exam-creator-container">
      {loadingExam ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading exam data...</p>
        </div>
      ) : (
        <>
          <div className="exam-creator-header">
            <button onClick={() => navigate("/admin/dashboard")} className="btn-back">
              ‚Üê Back to Dashboard
            </button>
            <h1>{isEditMode ? "‚úèÔ∏è Edit Exam" : "üìù Create New Exam"}</h1>
          </div>

          <form onSubmit={handleSubmit} className="exam-creator-form">
            {/* Basic Info */}
            <div className="form-section">
              <h2>Basic Information</h2>
              
              <div className="form-group">
                <label>Exam Title *</label>
                <input
                  type="text"
                  placeholder="e.g., JavaScript Fundamentals Quiz"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label>Duration (minutes) *</label>
                <input
                  type="number"
                  min="1"
                  max="180"
                  placeholder="30"
                  value={durationMins}
                  onChange={(e) => setDurationMins(parseInt(e.target.value))}
                  className="form-input"
                  required
                />
              </div>
            </div>

        {/* Questions */}
        <div className="form-section">
          <div className="questions-header">
            <h2>Questions ({questions.length})</h2>
            <button
              type="button"
              onClick={handleAddQuestion}
              className="btn-add-question"
            >
              ‚ûï Add Question
            </button>
          </div>

          {questions.map((question, qIndex) => (
            <div key={qIndex} className="question-card">
              <div className="question-header">
                <h3>Question {qIndex + 1}</h3>
                {questions.length > 1 && (
                  <button
                    type="button"
                    onClick={() => handleRemoveQuestion(qIndex)}
                    className="btn-remove"
                  >
                    üóëÔ∏è Remove
                  </button>
                )}
              </div>

              <div className="form-group">
                <label>Question Text *</label>
                <textarea
                  placeholder="Enter your question here..."
                  value={question.text}
                  onChange={(e) =>
                    handleQuestionChange(qIndex, "text", e.target.value)
                  }
                  className="form-textarea"
                  rows="3"
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Question Image (Optional)</label>
                <div className="image-upload-container">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => handleImageUpload(qIndex, e)}
                    className="form-input"
                  />
                  {question.imagePreview && (
                    <div className="image-preview">
                      <img src={question.imagePreview} alt="Question preview" />
                      <button 
                        type="button" 
                        onClick={() => handleRemoveImage(qIndex)}
                        className="btn-remove-image"
                      >
                        Remove Image
                      </button>
                    </div>
                  )}
                </div>
              </div>

              <div className="options-section">
                <label>Options *</label>
                {question.options.map((option, oIndex) => (
                  <div key={oIndex} className="option-row">
                    <input
                      type="radio"
                      name={`correct-${qIndex}`}
                      checked={question.correctOption === oIndex}
                      onChange={() => handleCorrectOptionChange(qIndex, oIndex)}
                      className="radio-input"
                    />
                    <input
                      type="text"
                      placeholder={`Option ${oIndex + 1}`}
                      value={option}
                      onChange={(e) =>
                        handleOptionChange(qIndex, oIndex, e.target.value)
                      }
                      className="form-input option-input"
                      required
                    />
                    {question.correctOption === oIndex && (
                      <span className="correct-indicator">‚úì Correct</span>
                    )}
                  </div>
                ))}
                <p className="helper-text">
                  üí° Select the radio button to mark the correct answer
                </p>
              </div>
            </div>
          ))}
        </div>

            {/* Submit */}
            <div className="form-actions">
              <button
                type="button"
                onClick={() => navigate("/admin/dashboard")}
                className="btn-cancel"
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn-submit"
                disabled={loading}
              >
                {loading ? (isEditMode ? "Updating..." : "Creating...") : (isEditMode ? "‚úì Update Exam" : "‚úì Create Exam")}
              </button>
            </div>
          </form>
        </>
      )}
    </div>
  );
}
