import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ResultPage.css";

export default function ResultPage({ onLogout }) {
  const { submissionId } = useParams();
  const [result, setResult] = useState(null);
  const [proctorLogs, setProctorLogs] = useState([]);
  const [logSummary, setLogSummary] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleGoToDashboard = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    if (userData?.role === "student") navigate("/student/dashboard");
    else if (userData?.role === "admin") navigate("/admin/dashboard");
    else navigate("/");
  };

  const handleLogoutAndGoHome = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("name");

    if (onLogout) onLogout();
    alert("‚úÖ You have been logged out successfully!");
    navigate("/");
    window.location.reload();
  };

  useEffect(() => {
    const fetchResult = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setError("No authentication token found. Please login.");
          setLoading(false);
          return;
        }

        console.log("üìä Fetching result for submission:", submissionId);

        // Fetch exam result
        const response = await fetch(
          `http://localhost:4000/api/exam/result/${submissionId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (!response.ok) throw new Error(`Failed to fetch result: ${response.status}`);
        const data = await response.json();
        
        console.log("‚úÖ Result data received:");
        console.log("   User ID:", data.userId);
        console.log("   Exam ID:", data.examId);
        console.log("   Score:", data.score);
        
        setResult(data);

        // FIXED: Fetch unified proctoring logs for THIS exam only
        if (data.userId && data.examId) {
          try {
            console.log(`üîç Fetching ALL proctoring logs for THIS exam`);
            console.log(`   User: ${data.userId}`);
            console.log(`   Exam: ${data.examId}`);
            
            // Use the new unified endpoint
            const proctorRes = await fetch(
              `http://localhost:4000/api/exam/all-proctor-logs/${data.userId}/${data.examId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );

            if (proctorRes.ok) {
              const proctorData = await proctorRes.json();
              console.log("üìã Proctoring logs received:");
              console.log("   Total logs:", proctorData.proctorLogs?.length || 0);
              console.log("   Summary:", proctorData.summary);
              
              setProctorLogs(proctorData.proctorLogs || []);
              setLogSummary(proctorData.summary || null);
              
              if (proctorData.proctorLogs?.length === 0) {
                console.log("‚ú® No violations detected - clean exam!");
              }
            } else {
              console.warn("‚ö†Ô∏è Could not fetch proctoring logs:", proctorRes.status);
              
              // Fallback to old endpoint if new one doesn't exist yet
              console.log("üîÑ Trying fallback endpoint...");
              const fallbackRes = await fetch(
                `http://localhost:4000/api/exam/face-logs/${data.userId}/${data.examId}`,
                { headers: { Authorization: `Bearer ${token}` } }
              );
              
              if (fallbackRes.ok) {
                const fallbackData = await fallbackRes.json();
                console.log("üìã Fallback logs received:", fallbackData.faceLogs?.length || 0);
                setProctorLogs(fallbackData.faceLogs || []);
              }
            }
          } catch (proctorError) {
            console.warn("‚ö†Ô∏è Error fetching proctoring logs:", proctorError);
          }
        } else {
          console.warn("‚ö†Ô∏è Missing userId or examId in result");
        }

        setLoading(false);
      } catch (error) {
        console.error("‚ùå Error fetching result:", error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchResult();
  }, [submissionId]);

  if (loading) {
    return (
      <div className="result-loading">
        <div className="loading-spinner"></div>
        <h3>Loading your results...</h3>
        <p>Fetching exam results and proctoring logs...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3 style={{ color: "red" }}>‚ùå Error</h3>
        <p>{error}</p>
        <button onClick={handleLogoutAndGoHome} className="btn">
          Go to Login
        </button>
      </div>
    );
  }

  if (!result) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3>No result found</h3>
        <button onClick={handleLogoutAndGoHome} className="btn">
          Go to Login
        </button>
      </div>
    );
  }

  const percentage = ((result.score / result.totalQuestions) * 100).toFixed(1);
  const isPassed = percentage >= 60;

  return (
    <div className="result-container">
      <div className="result-header">
        <h2>üìä Exam Results</h2>
        <p className="result-exam-title">{result.examTitle || 'Exam'}</p>
        <p className="result-submission-date">
          Submitted: {result.submittedAt ? new Date(result.submittedAt).toLocaleString() : 'N/A'}
        </p>
      </div>

      <div className="result-score-card">
        <div className="result-score-display">
          {result.score}/{result.totalQuestions}
        </div>
        <div className="result-score-detail">Questions Answered Correctly</div>
        <div className="result-percentage">{percentage}%</div>
        <div className={`result-pass-badge ${isPassed ? "passed" : "failed"}`}>
          {isPassed ? "‚úÖ Passed" : "‚ùå Failed"}
        </div>
      </div>

      <div className="result-section">
        <h3>üìù Question Review</h3>
        {result.questions.map((q, i) => {
          const userAnswerIndex = result.answers[i];
          const isCorrect = userAnswerIndex === q.correctOption;

          return (
            <div
              key={i}
              className={`result-question-item ${isCorrect ? "correct" : "incorrect"}`}
            >
              <div className="result-question-number">Question {i + 1}</div>
              <div className="result-question-text">{q.text}</div>

              <div className="result-answer-row">
                <div className="result-answer-item">
                  <span className="result-answer-label">Your Answer:</span>
                  <span
                    className={`result-answer-value ${
                      isCorrect ? "correct-answer" : "wrong-answer"
                    }`}
                  >
                    {userAnswerIndex !== null && userAnswerIndex !== undefined 
                      ? q.options[userAnswerIndex] 
                      : "Not Answered"}
                  </span>
                </div>

                <div className="result-answer-item">
                  <span className="result-answer-label">Correct Answer:</span>
                  <span className="result-answer-value correct-answer">
                    {q.options[q.correctOption]}
                  </span>
                </div>
              </div>

              <span className="result-status-icon">{isCorrect ? "‚úÖ" : "‚ùå"}</span>
            </div>
          );
        })}
      </div>

      {/* FIXED: Unified Proctoring Section - Shows ALL types of violations */}
      {proctorLogs.length > 0 ? (
        <div className="result-proctoring-section">
          <h3>üëÄ Proctoring Report - Current Exam Only</h3>
          
          {logSummary && (
            <div className="proctoring-summary">
              <div className="proctoring-stat">
                <span className="stat-icon">üìä</span>
                <span className="stat-value">{logSummary.total}</span>
                <span className="stat-label">Total Violations</span>
              </div>
              <div className="proctoring-stat">
                <span className="stat-icon">üë•</span>
                <span className="stat-value">{logSummary.multipleFaces}</span>
                <span className="stat-label">Multiple Faces</span>
              </div>
              <div className="proctoring-stat">
                <span className="stat-icon">üö®</span>
                <span className="stat-value">{logSummary.identityIssues}</span>
                <span className="stat-label">Identity Issues</span>
              </div>
              <div className="proctoring-stat">
                <span className="stat-icon">‚ùå</span>
                <span className="stat-value">{logSummary.noFaceDetections}</span>
                <span className="stat-label">No Face Detected</span>
              </div>
              <div className="proctoring-stat">
                <span className="stat-icon">üì±</span>
                <span className="stat-value">{logSummary.mobileDetections}</span>
                <span className="stat-label">Mobile Detected</span>
              </div>
            </div>
          )}

          <ul className="result-face-logs-list">
            {proctorLogs.map((log, index) => {
              const logType = log.type || 'default';
              const logMessage = log.message || log.details || log.data?.message || 'Proctoring event detected';
              
              return (
                <li key={index} className={`result-face-log-item ${logType}`}>
                  <span className="result-face-log-icon">
                    {logType === 'multiple-face' ? 'üë•' :
                     logType === 'tab-switch' ? 'üîÑ' :
                     logType === 'no-face' ? '‚ùå' :
                     logType === 'identity-mismatch' ? 'üö®' :
                     logType === 'phone-detected' ? 'üì±' :
                     '‚ö†Ô∏è'}
                  </span>
                  <div className="result-face-log-details">
                    <span className="result-face-log-time">
                      {new Date(log.timestamp).toLocaleString()}
                    </span>
                    <span className="result-face-log-message">
                      {logMessage}
                    </span>
                    {log.confidence && (
                      <span className="result-face-log-confidence">
                        Confidence: {(log.confidence * 100).toFixed(1)}%
                      </span>
                    )}
                    {log.severity && (
                      <span className={`result-face-log-severity ${log.severity}`}>
                        {log.severity === 'critical' ? 'üî¥ Critical' :
                         log.severity === 'high' ? 'üü† High' :
                         log.severity === 'medium' ? 'üü° Medium' :
                         'üü¢ Low'}
                      </span>
                    )}
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      ) : (
        <div className="result-proctoring-section">
          <h3>üëÄ Proctoring Report</h3>
          <div className="no-violations">
            <span className="success-icon">‚úÖ</span>
            <p>No proctoring violations detected during this exam!</p>
            <p className="sub-text">You followed all exam rules properly. Excellent work!</p>
          </div>
        </div>
      )}

      {/* Performance Summary */}
      <div className="result-section performance-summary">
        <h3>üìà Performance Summary</h3>
        <div className="performance-grid">
          <div className="performance-item">
            <span className="performance-label">Correct Answers</span>
            <span className="performance-value correct">{result.score}</span>
          </div>
          <div className="performance-item">
            <span className="performance-label">Wrong Answers</span>
            <span className="performance-value wrong">
              {result.totalQuestions - result.score}
            </span>
          </div>
          <div className="performance-item">
            <span className="performance-label">Accuracy</span>
            <span className="performance-value">{percentage}%</span>
          </div>
          <div className="performance-item">
            <span className="performance-label">Status</span>
            <span className={`performance-value ${isPassed ? 'passed' : 'failed'}`}>
              {isPassed ? 'Passed' : 'Failed'}
            </span>
          </div>
        </div>
      </div>

      <div className="result-actions">
        <button
          onClick={handleGoToDashboard}
          className="result-btn result-btn-secondary"
        >
          üè† Return to Dashboard
        </button>

        <button
          onClick={handleLogoutAndGoHome}
          className="result-btn result-btn-primary"
        >
          üîì Logout & Return to Login
        </button>
      </div>

      <div className="result-footer">
        <p className="footer-text">
          ‚ÑπÔ∏è If you have concerns about your result or proctoring logs, please contact your administrator.
        </p>
        <p className="footer-text" style={{ marginTop: '0.5rem', fontSize: '0.85rem', color: '#6b7280' }}>
          Exam ID: {result.examId} | Logs shown: {proctorLogs.length}
        </p>
      </div>
    </div>
  );
}
