// =============================================
// FIXED ResultPage.js - Shows Correct Current Exam Results
// =============================================

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ResultPage.css";

export default function ResultPage({ onLogout, isAdmin = false }) {
  const { submissionId } = useParams();
  const [result, setResult] = useState(null);
  const [faceLogs, setFaceLogs] = useState([]);
  const [mobileLogs, setMobileLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // IMPORTANT: Clear any cached data when component mounts
  useEffect(() => {
    console.log("üîÑ Loading result for submission:", submissionId);
    
    // Clear previous state
    setResult(null);
    setFaceLogs([]);
    setMobileLogs([]);
    setError(null);
    setLoading(true);

    // Fetch fresh data
    fetchResult();
  }, [submissionId]); // Re-fetch if submissionId changes

  const fetchResult = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("No authentication token found. Please login.");
        setLoading(false);
        return;
      }

      console.log("üì° Fetching result from server...");

      // Fetch exam result with cache-busting
      const timestamp = Date.now();
      const response = await fetch(
        `http://localhost:4000/api/exam/result/${submissionId}?t=${timestamp}`,
        {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache',
            'Expires': '0'
          },
        }
      );

      if (!response.ok) {
        throw new Error(`Failed to fetch result: ${response.status}`);
      }

      const data = await response.json();
      console.log("‚úÖ Result received:", data);

      // Validate data
      if (!data || !data.questions || !data.answers) {
        throw new Error("Invalid result data received");
      }

      setResult(data);

      // Fetch face verification logs if userId and examId exist
      if (data.userId && data.examId) {
        await fetchFaceLogs(data.userId, data.examId);
        await fetchMobileLogs(data.userId, data.examId);
      }

      setLoading(false);
    } catch (error) {
      console.error("‚ùå Error fetching result:", error);
      setError(error.message);
      setLoading(false);
    }
  };

  const fetchFaceLogs = async (userId, examId) => {
    try {
      const token = localStorage.getItem("token");
      const timestamp = Date.now();
      const faceRes = await fetch(
        `http://localhost:4000/api/exam/face-verification-logs/${userId}/${examId}?t=${timestamp}`,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Cache-Control': 'no-cache'
          } 
        }
      );

      if (faceRes.ok) {
        const faceData = await faceRes.json();
        console.log("‚úÖ Face logs received:", faceData.verificationLogs?.length || 0);
        setFaceLogs(faceData.verificationLogs || []);
      }
    } catch (faceError) {
      console.warn("Could not fetch face logs:", faceError);
    }
  };

  const fetchMobileLogs = async (userId, examId) => {
    try {
      const token = localStorage.getItem("token");
      const timestamp = Date.now();
      const mobileRes = await fetch(
        `http://localhost:4000/api/exam/mobile-detection-logs/${userId}/${examId}?t=${timestamp}`,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Cache-Control': 'no-cache'
          } 
        }
      );

      if (mobileRes.ok) {
        const mobileData = await mobileRes.json();
        console.log("‚úÖ Mobile logs received:", mobileData.mobileLogs?.length || 0);
        setMobileLogs(mobileData.mobileLogs || []);
      }
    } catch (mobileError) {
      console.warn("Could not fetch mobile logs:", mobileError);
    }
  };

  const handleLogoutAndGoHome = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("name");

    if (onLogout) onLogout();
    alert("‚úÖ You have been logged out successfully!");
    navigate("/");
    window.location.reload();
  };

  const handleBackToDashboard = () => {
    if (isAdmin) {
      navigate("/admin/dashboard");
    } else {
      navigate("/student/dashboard");
    }
  };

  // Loading state
  if (loading) {
    return (
      <div className="result-loading">
        <div className="loading-spinner"></div>
        <h3>Loading your results...</h3>
        <p>Please wait while we fetch your exam results...</p>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3 style={{ color: "red" }}>‚ùå Error</h3>
        <p>{error}</p>
        <button onClick={handleBackToDashboard} className="btn">
          Back to Dashboard
        </button>
      </div>
    );
  }

  // No result found
  if (!result) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3>No result found</h3>
        <p>Submission ID: {submissionId}</p>
        <button onClick={handleBackToDashboard} className="btn">
          Back to Dashboard
        </button>
      </div>
    );
  }

  // Calculate results
  const percentage = ((result.score / result.totalQuestions) * 100).toFixed(1);
  const isPassed = percentage >= 60;

  // Count violations
  const faceFailures = faceLogs.filter(log => log.verificationStatus === 'failed').length;
  const mobileDetections = mobileLogs.length;
  const totalViolations = faceFailures + mobileDetections;

  return (
    <div className="result-container">
      <div className="result-header">
        <h2>üìä Exam Results</h2>
        <p className="submission-id">Submission ID: {submissionId}</p>
      </div>

      {/* Score Card */}
      <div className="result-score-card">
        <div className="result-score-display">
          {result.score}/{result.totalQuestions}
        </div>
        <div className="result-score-detail">Questions Answered Correctly</div>
        <div className="result-percentage">{percentage}%</div>
        <div className={`result-pass-badge ${isPassed ? "passed" : "failed"}`}>
          {isPassed ? "‚úÖ Passed" : "‚ùå Failed"}
        </div>
      </div>

      {/* Violation Summary */}
      {totalViolations > 0 && (
        <div className="violation-summary">
          <h3>‚ö†Ô∏è Proctoring Violations Detected</h3>
          <div className="violation-stats">
            {faceFailures > 0 && (
              <div className="violation-stat">
                <span className="violation-icon">üîç</span>
                <span className="violation-count">{faceFailures}</span>
                <span className="violation-label">Identity Failures</span>
              </div>
            )}
            {mobileDetections > 0 && (
              <div className="violation-stat">
                <span className="violation-icon">üì±</span>
                <span className="violation-count">{mobileDetections}</span>
                <span className="violation-label">Mobile Detections</span>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Question Review */}
      <div className="result-section">
        <h3>üìù Question Review</h3>
        {result.questions.map((q, i) => {
          const userAnswerIndex = result.answers[i];
          const isCorrect = userAnswerIndex === q.correctOption;

          console.log(`Question ${i + 1}:`, {
            userAnswer: userAnswerIndex,
            correctAnswer: q.correctOption,
            isCorrect
          });

          return (
            <div
              key={i}
              className={`result-question-item ${isCorrect ? "correct" : "incorrect"}`}
            >
              <div className="result-question-number">Question {i + 1}</div>
              <div className="result-question-text">{q.text}</div>

              <div className="result-options-display">
                {q.options.map((option, optIdx) => {
                  const isUserAnswer = userAnswerIndex === optIdx;
                  const isCorrectAnswer = q.correctOption === optIdx;
                  
                  let optionClass = "result-option";
                  if (isCorrectAnswer) optionClass += " correct-answer-option";
                  if (isUserAnswer && !isCorrectAnswer) optionClass += " wrong-answer-option";
                  if (isUserAnswer) optionClass += " user-selected";

                  return (
                    <div key={optIdx} className={optionClass}>
                      <span className="option-letter">{String.fromCharCode(65 + optIdx)}.</span>
                      <span className="option-text">{option}</span>
                      {isCorrectAnswer && <span className="option-badge correct">‚úì Correct</span>}
                      {isUserAnswer && !isCorrectAnswer && <span className="option-badge wrong">Your Answer</span>}
                    </div>
                  );
                })}
              </div>

              <div className="result-answer-summary">
                <div className="answer-item">
                  <span className="answer-label">Your Answer:</span>
                  <span className={`answer-value ${isCorrect ? "correct" : "wrong"}`}>
                    {userAnswerIndex !== undefined && userAnswerIndex !== null 
                      ? `${String.fromCharCode(65 + userAnswerIndex)}. ${q.options[userAnswerIndex]}`
                      : "Not Answered"}
                  </span>
                </div>

                {!isCorrect && (
                  <div className="answer-item">
                    <span className="answer-label">Correct Answer:</span>
                    <span className="answer-value correct">
                      {String.fromCharCode(65 + q.correctOption)}. {q.options[q.correctOption]}
                    </span>
                  </div>
                )}
              </div>

              <span className="result-status-icon">{isCorrect ? "‚úÖ" : "‚ùå"}</span>
            </div>
          );
        })}
      </div>

      {/* Proctoring Logs */}
      {(faceLogs.length > 0 || mobileLogs.length > 0) && (
        <div className="result-proctoring-section">
          <h3>üëÄ Proctoring Report</h3>
          
          {faceLogs.length > 0 && (
            <div className="log-section">
              <h4>üîç Face Verification Logs</h4>
              <ul className="result-logs-list">
                {faceLogs.map((log, index) => (
                  <li key={index} className={`result-log-item ${log.verificationStatus}`}>
                    <span className="log-time">
                      {new Date(log.timestamp).toLocaleString()}
                    </span>
                    <span className="log-status">
                      {log.verificationStatus === 'verified' && '‚úì Verified'}
                      {log.verificationStatus === 'failed' && '‚úó Failed'}
                      {log.verificationStatus === 'no_face' && '‚ö† No Face'}
                    </span>
                    <span className="log-details">{log.details}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {mobileLogs.length > 0 && (
            <div className="log-section">
              <h4>üì± Mobile Phone Detections</h4>
              <ul className="result-logs-list">
                {mobileLogs.map((log, index) => (
                  <li key={index} className="result-log-item mobile">
                    <span className="log-time">
                      {new Date(log.timestamp).toLocaleString()}
                    </span>
                    <span className="log-status">üì± Phone Detected</span>
                    <span className="log-confidence">
                      {(log.confidence * 100).toFixed(1)}% confidence
                    </span>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* Action Buttons */}
      <div className="result-actions">
        <button
          onClick={handleBackToDashboard}
          className="result-btn result-btn-secondary"
        >
          ‚Üê Back to Dashboard
        </button>
        {!isAdmin && (
          <button
            onClick={handleLogoutAndGoHome}
            className="result-btn result-btn-primary"
          >
            üîì Logout
          </button>
        )}
      </div>
    </div>
  );
}
