// =============================================
// ExamPage.js - Complete and Fixed Version
// =============================================

import React, { useEffect, useState, useRef } from "react";
import * as faceapi from "face-api.js";
import { useNavigate, useParams } from "react-router-dom";
import "./ExamPage.css";

export default function ExamPage({ user, onLogout }) {
  const { examId } = useParams();
  const videoRef = useRef(null);
  const [tabSwitchCount, setTabSwitchCount] = useState(0);
  const [warningShown, setWarningShown] = useState(false);
  const [timeLeft, setTimeLeft] = useState(600);
  const [logs, setLogs] = useState([]);
  const [paper, setPaper] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [currentQ, setCurrentQ] = useState(0);

  const [registeredDescriptor, setRegisteredDescriptor] = useState(null);
  const [faceVerificationStatus, setFaceVerificationStatus] = useState("loading");
  const [verificationFailures, setVerificationFailures] = useState(0);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [policy, setPolicy] = useState(null);
  const [phase, setPhase] = useState("precheck"); // precheck | running

  const multipleFaceActive = useRef(false);
  const startTimeRef = useRef(null);
  const isSubmitting = useRef(false);
  const verificationInterval = useRef(null);
  const navigate = useNavigate();

  // ‚úÖ Stop camera utility
  const stopCamera = () => {
    const stream = videoRef.current?.srcObject;
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
      console.log("üõë Camera stopped");
    }
  };

  // ‚úÖ Load registered face descriptor
  useEffect(() => {
    loadRegisteredFace();
  }, []);

  const loadRegisteredFace = async () => {
    try {
      console.log("üîÑ Loading registered face descriptor...");
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:4000/api/auth/face-descriptor", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.faceDescriptor && Array.isArray(data.faceDescriptor) && data.faceDescriptor.length === 128) {
          setRegisteredDescriptor(new Float32Array(data.faceDescriptor));
          setFaceVerificationStatus("ready");
          console.log("‚úÖ Face verification system ready!");
        } else {
          alert("Invalid face descriptor. Please re-register your face.");
          navigate("/face-registration");
        }
      } else {
        alert("‚ö†Ô∏è Please register your face before taking exams!");
        navigate("/face-registration");
      }
    } catch (error) {
      console.error("‚ùå Error loading face descriptor:", error);
      setFaceVerificationStatus("error");
      alert("Failed to load face verification. Please try again.");
    }
  };

  // ‚úÖ Load exam paper & models
  useEffect(() => {
    const loadModelsAndExam = async () => {
      try {
        console.log("üîÑ Loading models...");
        const MODEL_URL = process.env.PUBLIC_URL + "/models";

        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        ]);

        setModelsLoaded(true);

        const res = await fetch(`http://localhost:4000/api/exam/paper/${examId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });

        if (!res.ok) throw new Error(`Server error: ${res.status}`);
        const data = await res.json();

        if (!data?.questions?.length) throw new Error("No questions in exam");

        setPaper(data);
        setAnswers(new Array(data.questions.length).fill(null));
        setTimeLeft((data.durationMins || 10) * 60);

        const stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 640, height: 480 },
        });
        if (videoRef.current) videoRef.current.srcObject = stream;

        // Start exam session (pre-exam setup)
        const startRes = await fetch("http://localhost:4000/api/exam/start-session", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({ examId }),
        });
        if (startRes.ok) {
          const startData = await startRes.json();
          setPolicy(startData.policy || null);
        }
      } catch (err) {
        console.error("‚ùå Setup failed:", err);
        alert(`Failed to load exam: ${err.message}`);
        navigate("/student/dashboard");
      }
    };

    loadModelsAndExam();
    return () => stopCamera(); // ‚úÖ Cleanup on unmount
  }, [examId, navigate]);

  // ‚úÖ Face verification monitoring
  useEffect(() => {
    if (!paper || !registeredDescriptor || !modelsLoaded) return;

    verificationInterval.current = setInterval(async () => {
      await verifyFaceIdentity();
    }, (policy?.verifyIntervalMs ?? 5000));

    setTimeout(() => verifyFaceIdentity(), 2000);

    return () => {
      clearInterval(verificationInterval.current);
      console.log("üõë Stopped face verification");
    };
  }, [paper, registeredDescriptor, modelsLoaded, policy]);

  const verifyFaceIdentity = async () => {
    if (!videoRef.current || !registeredDescriptor) return;

    try {
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions({ inputSize: 224 }))
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        setLogs(prev => [...prev, `‚ö†Ô∏è No face at ${new Date().toLocaleTimeString()}`]);
        // Notify backend (no face)
        try {
          await fetch("http://localhost:4000/api/exam/verify-identity", {
            method: "POST",
            headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
            body: JSON.stringify({ examId, reason: "no_face" }),
          });
        } catch {}
        return;
      }

      const distance = faceapi.euclideanDistance(registeredDescriptor, detection.descriptor);
      const threshold = 1 - (policy?.faceMatchThreshold ?? 0.85); // since we compute confidence as 1 - distance
      const isMatch = distance <= threshold;
      const confidence = Math.max(0, 1 - distance);

      if (isMatch) {
        setFaceVerificationStatus("verified");
        if (phase === "precheck") setPhase("running");
        // Report success
        try {
          await fetch("http://localhost:4000/api/exam/verify-identity", {
            method: "POST",
            headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
            body: JSON.stringify({ examId, similarity: confidence }),
          });
        } catch {}
      } else {
        const newCount = verificationFailures + 1;
        setVerificationFailures(newCount);
        // Report failure and follow backend policy
        try {
          const resp = await fetch("http://localhost:4000/api/exam/verify-identity", {
            method: "POST",
            headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
            body: JSON.stringify({ examId, similarity: confidence, reason: "mismatch" }),
          });
          const data = await resp.json();
          if (data?.shouldBlock) {
            alert("üö® Identity verification failed repeatedly. Auto-submitting exam...");
            handleSubmit();
          } else {
            alert(`üö® Identity mismatch detected! Attempt ${newCount} of ${(policy?.maxConsecutiveFailures ?? 3)}.`);
          }
        } catch {
          alert(`üö® Identity mismatch detected! Attempt ${newCount}.`);
        }
      }
    } catch (error) {
      console.error("Verification error:", error);
    }
  };

  // ‚úÖ Tab switching detection
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.hidden) setTabSwitchCount(prev => prev + 1);
    };
    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => document.removeEventListener("visibilitychange", handleVisibilityChange);
  }, []);

  // ‚úÖ Tab warning logic
  useEffect(() => {
    if (tabSwitchCount === 2 && !warningShown) {
      alert("‚ö†Ô∏è Warning: You switched tabs! One more switch will auto-submit your exam.");
      setWarningShown(true);
    } else if (tabSwitchCount >= 3) {
      alert("‚ùå Too many tab switches. Auto-submitting your exam...");
      handleSubmit();
    }
  }, [tabSwitchCount, warningShown]);

  // ‚úÖ Timer
  useEffect(() => {
    if (timeLeft <= 0) {
      alert("‚è∞ Time‚Äôs up! Auto-submitting your exam...");
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  // ‚úÖ Multiple face detection
  useEffect(() => {
    if (!paper) return;
    const interval = setInterval(async () => {
      if (!videoRef.current) return;
      try {
        const detections = await faceapi.detectAllFaces(
          videoRef.current,
          new faceapi.TinyFaceDetectorOptions()
        );
        if (detections.length > 1) {
          if (!multipleFaceActive.current) {
            multipleFaceActive.current = true;
            startTimeRef.current = new Date();
            setLogs(prev => [...prev, `Multiple faces detected at ${startTimeRef.current.toLocaleTimeString()}`]);
          }
        } else if (multipleFaceActive.current) {
          multipleFaceActive.current = false;
          const endTime = new Date();
          const duration = Math.round((endTime - startTimeRef.current) / 1000);
          setLogs(prev => [...prev, `Multiple faces ended at ${endTime.toLocaleTimeString()} (${duration}s)`]);
        }
      } catch (err) {
        console.error("Face detection error:", err);
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [paper]);

  // ‚úÖ Environment restrictions (disable copy/paste/context menu)
  useEffect(() => {
    const prevent = (e) => e.preventDefault();
    document.addEventListener('copy', prevent);
    document.addEventListener('paste', prevent);
    document.addEventListener('cut', prevent);
    document.addEventListener('contextmenu', prevent);
    return () => {
      document.removeEventListener('copy', prevent);
      document.removeEventListener('paste', prevent);
      document.removeEventListener('cut', prevent);
      document.removeEventListener('contextmenu', prevent);
    };
  }, []);

  // ‚úÖ Live proctoring logs from server
  useEffect(() => {
    if (!paper) return;
    const interval = setInterval(async () => {
      try {
        const resp = await fetch(`http://localhost:4000/api/exam/live-logs/${examId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        if (resp.ok) {
          const data = await resp.json();
          if (Array.isArray(data?.logs)) {
            setLogs(data.logs.map(l => `${new Date(l.timestamp).toLocaleTimeString()} - ${l.type}: ${l.details}`));
          }
        }
      } catch {}
    }, 10000);
    return () => clearInterval(interval);
  }, [paper, examId]);

  // ‚úÖ Request fullscreen once exam starts
  useEffect(() => {
    if (phase !== 'running') return;
    const el = document.documentElement;
    if (el.requestFullscreen) el.requestFullscreen().catch(() => {});
  }, [phase]);

  // ‚úÖ Idle detection (no input for 60s)
  useEffect(() => {
    let idleTimer = null;
    const reset = () => {
      if (idleTimer) clearTimeout(idleTimer);
      idleTimer = setTimeout(async () => {
        setLogs(prev => [
          `‚ö†Ô∏è Idle for 60s at ${new Date().toLocaleTimeString()}`,
          ...prev,
        ]);
        try {
          await fetch("http://localhost:4000/api/exam/proctor-event", {
            method: "POST",
            headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
            body: JSON.stringify({ examId, type: "idle", data: { duration: 60 } }),
          });
        } catch {}
      }, 60000);
    };
    window.addEventListener('mousemove', reset);
    window.addEventListener('keydown', reset);
    reset();
    return () => {
      window.removeEventListener('mousemove', reset);
      window.removeEventListener('keydown', reset);
      if (idleTimer) clearTimeout(idleTimer);
    };
  }, [examId]);

  // ‚úÖ Network status logs
  useEffect(() => {
    const onChange = async () => {
      const online = navigator.onLine;
      setLogs(prev => [
        `${new Date().toLocaleTimeString()} - network: ${online ? 'online' : 'offline'}`,
        ...prev,
      ]);
      try {
        await fetch("http://localhost:4000/api/exam/proctor-event", {
          method: "POST",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
          body: JSON.stringify({ examId, type: "network", data: { online } }),
        });
      } catch {}
    };
    window.addEventListener('online', onChange);
    window.addEventListener('offline', onChange);
    return () => {
      window.removeEventListener('online', onChange);
      window.removeEventListener('offline', onChange);
    };
  }, [examId]);

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${min}:${sec < 10 ? "0" : ""}${sec}`;
  };

  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  // ‚úÖ Submit handler (corrected)
  const handleSubmit = async () => {
    if (isSubmitting.current) return;
    if (!paper) return alert("Exam not loaded!");

    if (tabSwitchCount < 3 && timeLeft > 0) {
      if (!window.confirm("Are you sure you want to submit?")) return;
    }

    isSubmitting.current = true;

    try {
      const response = await fetch("http://localhost:4000/api/exam/submit", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          userId: user._id,
          examId,
          answers,
        }),
      });

      if (!response.ok) throw new Error(`Submit failed: ${response.status}`);
      const data = await response.json();

      if (data.submissionId) {
        stopCamera();
        navigate(`/result/${data.submissionId}`);
      } else {
        throw new Error("No submission ID returned");
      }
    } catch (err) {
      console.error("Submit error:", err);
      alert("‚ùå Failed to submit exam. Try again.");
      isSubmitting.current = false;
    }
  };

  if (!paper || !paper.questions) {
    return (
      <div className="exam-loading">
        <h3>Loading exam...</h3>
        <p>Please wait while we prepare your exam.</p>
      </div>
    );
  }

  const question = paper.questions[currentQ];

  return (
    <div className="exam-page">
      <div className="debug-info">
        Models: {modelsLoaded ? "‚úÖ" : "‚ùå"} | Descriptor: {registeredDescriptor ? "‚úÖ" : "‚ùå"} | Status: {faceVerificationStatus} | Failures: {verificationFailures}/{policy?.maxConsecutiveFailures ?? 3}
      </div>

      <button
        onClick={() => {
          if (window.confirm("Exit exam? Progress will be lost!")) {
            stopCamera();
            if (user.role === "student") navigate("/student/dashboard");
            else if (user.role === "admin") navigate("/admin/dashboard");
            else navigate("/");
          }
        }}
        className="exam-back-btn"
      >
        ‚Üê Back to Dashboard
      </button>

      <div className="exam-body">
        {phase === "precheck" && (
          <div style={{
            position: "fixed", inset: 0, background: "rgba(0,0,0,0.7)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 10,
          }}>
            <div style={{ background: "#fff", padding: 20, borderRadius: 8, width: 480, maxWidth: "90%" }}>
              <h3>Pre-exam setup</h3>
              <ul>
                <li>Allow camera access</li>
                <li>Face must match your registered profile</li>
                <li>Avoid backlight and ensure only you are visible</li>
              </ul>
              <p style={{ marginTop: 10 }}>Waiting for identity verification...</p>
              <p>Current status: {faceVerificationStatus}</p>
              <p>Policy match threshold: {(policy?.faceMatchThreshold ?? 0.85)}</p>
            </div>
          </div>
        )}
        <div className="exam-main-content">
          <div className="exam-progress">
            Question {currentQ + 1} of {paper.questions.length}
          </div>

          <div className="exam-question">
            <h3>
              Q{currentQ + 1}: {question.text}
            </h3>
            <div className="exam-options">
              {question.options.map((opt, idx) => (
                <label
                  key={idx}
                  className={`exam-option-label ${answers[currentQ] === opt ? "selected" : ""}`}
                >
                  <input
                    type="radio"
                    name={`q${currentQ}`}
                    checked={answers[currentQ] === opt}
                    onChange={() => handleAnswerChange(currentQ, opt)}
                  />
                  {opt}
                </label>
              ))}
            </div>
          </div>

          <div className="exam-nav-buttons">
            {currentQ > 0 && (
              <button disabled={phase !== "running"} onClick={() => setCurrentQ(currentQ - 1)} className="exam-prev-btn">
                ‚¨ÖÔ∏è Previous
              </button>
            )}
            {currentQ < paper.questions.length - 1 ? (
              <button disabled={phase !== "running"} onClick={() => setCurrentQ(currentQ + 1)} className="exam-next-btn">
                Next ‚û°Ô∏è
              </button>
            ) : (
              <button onClick={handleSubmit} className="exam-submit-btn" disabled={isSubmitting.current || phase !== "running"}>
                {isSubmitting.current ? "‚è≥ Submitting..." : "üöÄ Submit Exam"}
              </button>
            )}
          </div>
        </div>

        <div className="exam-sidebar">
          <div className="exam-camera-preview">
            <h4>üì∑ Camera Monitor</h4>
            <video ref={videoRef} autoPlay muted style={{ width: "100%", maxWidth: "300px" }} />
            <p>üé• Camera Active</p>
          </div>

          <div className="exam-face-logs">
            <h4>üëÄ Face Detection Logs</h4>
            {logs.length > 0 ? (
              <ul>
                {logs.map((log, i) => (
                  <li key={i}>{log}</li>
                ))}
              </ul>
            ) : (
              <p>‚úÖ No issues detected</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
