import React, { useEffect, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as faceapi from "face-api.js";

export default function PreExamSetup() {
  const { examId } = useParams();
  const navigate = useNavigate();
  const videoRef = useRef(null);

  const [loading, setLoading] = useState(true);
  const [steps, setSteps] = useState([]);
  const [ready, setReady] = useState(false);
  const [policy, setPolicy] = useState(null);

  const addStep = (text, ok) => {
    setSteps(prev => [...prev, { text, ok }]);
  };

  useEffect(() => {
    const run = async () => {
      try {
        // Load models
        addStep("Loading face recognition models...", null);
        const MODEL_URL = process.env.PUBLIC_URL + "/models";
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        ]);
        addStep("Models loaded", true);

        // Camera access
        addStep("Requesting camera access...", null);
        const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 640, height: 480 } });
        if (videoRef.current) videoRef.current.srcObject = stream;
        await new Promise(r => setTimeout(r, 500));
        addStep("Camera access granted", true);

        // Fetch registered face descriptor
        addStep("Fetching registered face profile...", null);
        const descRes = await fetch("http://localhost:4000/api/auth/face-descriptor", { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } });
        if (!descRes.ok) throw new Error("Face not registered");
        const descData = await descRes.json();
        const registered = new Float32Array(descData.faceDescriptor);
        addStep("Registered face profile loaded", true);

        // Capture and compare once
        addStep("Verifying your face matches the registered profile...", null);
        const detection = await faceapi
          .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions({ inputSize: 224 }))
          .withFaceLandmarks()
          .withFaceDescriptor();
        if (!detection) throw new Error("No face detected. Adjust lighting and position.");
        const distance = faceapi.euclideanDistance(registered, detection.descriptor);
        const confidence = Math.max(0, 1 - distance);

        // Start session and get policy to know threshold
        const startRes = await fetch("http://localhost:4000/api/exam/start-session", {
          method: "POST",
          headers: { "Content-Type": "application/json", Authorization: `Bearer ${localStorage.getItem("token")}` },
          body: JSON.stringify({ examId }),
        });
        const startData = await startRes.json();
        setPolicy(startData.policy || null);

        const threshold = startData?.policy?.faceMatchThreshold ?? 0.85;
        if (confidence < threshold) {
          throw new Error(`Face mismatch. Confidence ${confidence.toFixed(2)} < threshold ${threshold}`);
        }
        addStep(`Identity verified (confidence ${confidence.toFixed(2)})`, true);

        // Environment checks
        addStep("Checking network status...", null);
        addStep(navigator.onLine ? "Online" : "Offline - please reconnect", navigator.onLine);

        addStep("Checking screen size (>= 1024x700 recommended)...", null);
        const okSize = window.innerWidth >= 1024 && window.innerHeight >= 700;
        addStep(`Screen ${window.innerWidth}x${window.innerHeight}`, okSize);

        setReady(true);
      } catch (err) {
        console.error(err);
        addStep(`Setup error: ${err.message}`, false);
      } finally {
        setLoading(false);
      }
    };
    run();

    return () => {
      const stream = videoRef.current?.srcObject;
      if (stream) stream.getTracks().forEach(t => t.stop());
    };
  }, [examId]);

  return (
    <div style={{ maxWidth: 900, margin: "40px auto", padding: 20 }}>
      <h2>Pre-Exam Setup</h2>
      <p>We’ll quickly check your environment and identity before starting.</p>

      <div style={{ display: 'flex', gap: 24, alignItems: 'flex-start', marginTop: 16 }}>
        <div style={{ flex: '0 0 340px' }}>
          <div style={{ border: '1px solid #ddd', borderRadius: 8, padding: 12 }}>
            <h4>Camera Preview</h4>
            <video ref={videoRef} autoPlay muted style={{ width: '100%', borderRadius: 8 }} />
          </div>
        </div>

        <div style={{ flex: 1 }}>
          <h4>Checks</h4>
          <ul>
            {steps.map((s, i) => (
              <li key={i} style={{ marginBottom: 8 }}>
                {s.ok === null ? '⏳' : s.ok ? '✅' : '❌'} {s.text}
              </li>
            ))}
          </ul>

          <div style={{ marginTop: 16, display: 'flex', gap: 12 }}>
            <button
              onClick={() => navigate(-1)}
              className="btn-secondary"
            >
              ← Back
            </button>
            <button
              disabled={!ready || loading}
              onClick={() => navigate(`/exam/${examId}`)}
              className="btn-start-exam"
            >
              Start Exam →
            </button>
          </div>

          {policy && (
            <p style={{ marginTop: 12, color: '#6b7280' }}>
              Identity threshold: {policy.faceMatchThreshold} • Verify every {Math.round((policy.verifyIntervalMs||5000)/1000)}s
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
