import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HomePage.css";

export default function HomePage({ user, onLogout }) {
  const navigate = useNavigate();
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAvailableExams();
  }, []);

  const fetchAvailableExams = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:4000/api/exam/available", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        const data = await res.json();
        setExams(data.exams || []);
      }
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleStartExam = (examId) => navigate(`/exam/${examId}`);
  const handleViewResults = () => navigate("/my-results");
  const handleLogoutClick = () => {
    if (window.confirm("Are you sure you want to logout?")) onLogout();
    navigate("/");
  };

  if (loading) {
    return (
      <div className="home-container" style={{ textAlign: "center", paddingTop: "5rem" }}>
        <div className="loading-spinner"></div>
        <h3>Loading exams...</h3>
      </div>
    );
  }

  return (
    <div className="home-container">
      <header className="home-header">
        <div>
          <h1>üìö Online Exam Portal</h1>
          <p>Welcome, <strong>{user?.name || "Student"}</strong>!</p>
        </div>
        <button onClick={handleLogoutClick} className="home-logout-btn">üîì Logout</button>
      </header>

      <section className="home-section">
        <h2>üìù Available Exams</h2>
        {exams.length === 0 ? (
          <p>No exams available at the moment.</p>
        ) : (
          <div className="home-exams-grid">
            {exams.map(exam => (
              <div key={exam._id} className="home-exam-card">
                <h3>{exam.title}</h3>
                <p>Questions: {exam.questions?.length || 0}</p>
                <p>Duration: {exam.durationMins || 30} mins</p>
                <button onClick={() => handleStartExam(exam._id)}>üöÄ Start Exam</button>
              </div>
            ))}
          </div>
        )}
      </section>

      <section className="home-section">
        <h2>‚ö° Quick Actions</h2>
        <div className="home-actions-grid">
          <div className="home-action-card" onClick={handleViewResults}>
            <h3>My Results</h3>
          </div>
        </div>
      </section>
    </div>
  );
}
