// =============================================
// ResultPage.js - Stable & Synced Version
// =============================================

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./ResultPage.css";

export default function ResultPage({ onLogout }) {
  const { submissionId } = useParams();
  const [result, setResult] = useState(null);
  const [faceLogs, setFaceLogs] = useState([]);
  const [proctorLogs, setProctorLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // --- Go to Dashboard ---
  const handleGoToDashboard = () => {
    const userData = JSON.parse(localStorage.getItem("user"));
    if (userData?.role === "student") navigate("/student/dashboard");
    else if (userData?.role === "admin") navigate("/admin/dashboard");
    else navigate("/");
  };

  // --- Logout ---
  const handleLogoutAndGoHome = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("name");
    if (onLogout) onLogout();
    alert("‚úÖ Logged out successfully!");
    navigate("/");
    window.location.reload();
  };
useEffect(() => {
  const fetchResult = async () => {
    const token = localStorage.getItem("token");
    const response = await fetch(`http://localhost:5000/api/exam/result/${submissionId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    const data = await response.json();
    console.log("Result:", data);
  };

  fetchResult();
}, [submissionId]);
  // --- Fetch Result & Logs ---
  useEffect(() => {
    const fetchResultAndLogs = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) throw new Error("No authentication token found. Please login.");

        // 1Ô∏è‚É£ Fetch exam result
        const res = await fetch(`http://localhost:4000/api/exam/result/${submissionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.ok) throw new Error(`Failed to fetch result (${res.status})`);
        const data = await res.json();
        setResult(data);

        // 2Ô∏è‚É£ Fetch Face Logs
        if (data.userId && data.examId) {
          try {
            const faceRes = await fetch(
              `http://localhost:4000/api/exam/face-logs/${data.userId}/${data.examId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );

            if (faceRes.ok) {
              const faceData = await faceRes.json();
              setFaceLogs(faceData.faceLogs || []);
            }
          } catch (faceErr) {
            console.warn("‚ö†Ô∏è Face log fetch failed:", faceErr);
          }

          // 3Ô∏è‚É£ Fetch Proctor Logs (specific to this exam)
          try {
            const proctorRes = await fetch(
              `http://localhost:4000/api/exam/proctor-logs/${data.userId}/${data.examId}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );

            if (proctorRes.ok) {
              const proctorData = await proctorRes.json();
              setProctorLogs(proctorData.logs || []);
            }
          } catch (proctorErr) {
            console.warn("‚ö†Ô∏è Proctor log fetch failed:", proctorErr);
          }
        }

        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching result:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchResultAndLogs();
  }, [submissionId]);

  // --- Loading ---
  if (loading) {
    return (
      <div className="result-loading">
        <div className="loading-spinner"></div>
        <h3>Loading your results...</h3>
      </div>
    );
  }

  // --- Error ---
  if (error) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3 style={{ color: "red" }}>‚ùå Error</h3>
        <p>{error}</p>
        <button onClick={handleLogoutAndGoHome} className="btn">
          Go to Login
        </button>
      </div>
    );
  }

  // --- No Result ---
  if (!result) {
    return (
      <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
        <h3>No result found</h3>
        <button onClick={handleLogoutAndGoHome} className="btn">
          Go to Login
        </button>
      </div>
    );
  }

  const percentage = ((result.score / result.totalQuestions) * 100).toFixed(1);
  const isPassed = percentage >= 60;

  return (
    <div className="result-container">
      <div className="result-header">
        <h2>üìä Exam Results</h2>
        <p>Submission ID: {submissionId}</p>
      </div>

      {/* --- Summary --- */}
      <div className="result-summary-card">
        <div className="summary-item">
          <span>Total Questions:</span> {result.totalQuestions}
        </div>
        <div className="summary-item">
          <span>Attempted:</span> {result.answers.filter(a => a !== null).length}
        </div>
        <div className="summary-item">
          <span>Correct:</span> {result.score}
        </div>
        <div className="summary-item">
          <span>Percentage:</span> {percentage}%
        </div>
        <div className={`summary-item status ${isPassed ? "passed" : "failed"}`}>
          Status: {isPassed ? "‚úÖ Passed" : "‚ùå Failed"}
        </div>
      </div>

      {/* --- Question Review --- */}
      <div className="result-section">
        <h3>üìù Question Review</h3>
        {result.questions.map((q, i) => {
          const userAnswer = result.answers?.[i] ?? null;
          const correctAnswer =
            q.correctOptionValue ||
            (typeof q.correctOption === "number" ? q.options[q.correctOption] : null);
          const isCorrect =
            String(userAnswer || "").trim() === String(correctAnswer || "").trim();

          return (
            <div
              key={i}
              className={`result-question-item ${isCorrect ? "correct" : "incorrect"}`}
            >
              <div className="result-question-number">Q{i + 1}</div>
              <div className="result-question-text">{q.text}</div>

              <div className="result-answer-row">
                <div className="result-answer-item">
                  <span className="result-answer-label">Your Answer:</span>
                  <span
                    className={`result-answer-value ${
                      isCorrect ? "correct-answer" : "wrong-answer"
                    }`}
                  >
                    {userAnswer || "Not Answered"}
                  </span>
                </div>
                <div className="result-answer-item">
                  <span className="result-answer-label">Correct Answer:</span>
                  <span className="result-answer-value correct-answer">
                    {correctAnswer || "Not Available"}
                  </span>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* --- Proctoring Section --- */}
      <div className="result-proctoring-section">
        <h3>üß† Proctoring Logs (Current Exam)</h3>

        {faceLogs.length === 0 && proctorLogs.length === 0 ? (
          <p className="result-no-logs">‚úÖ No suspicious activity detected.</p>
        ) : (
          <ul className="result-face-logs-list">
            {faceLogs.map((log, i) => (
              <li key={`face-${i}`} className="result-face-log-item">
                <span className="result-face-log-time">
                  {new Date(log.timestamp).toLocaleString()}
                </span>{" "}
                - <strong>Multiple Faces Detected</strong>
              </li>
            ))}

            {proctorLogs.map((log, i) => (
              <li key={`proctor-${i}`} className="result-face-log-item">
                <span className="result-face-log-time">
                  {new Date(log.ts || log.timestamp).toLocaleString()}
                </span>{" "}
                - <strong>{log.type}</strong>
                {log.data?.message ? ` (${log.data.message})` : ""}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* --- Buttons --- */}
      <div className="result-actions">
        <button onClick={handleGoToDashboard} className="result-btn result-btn-secondary">
          üè† Return to Dashboard
        </button>
        <button onClick={handleLogoutAndGoHome} className="result-btn result-btn-primary">
          üîì Logout & Return to Login
        </button>
      </div>
    </div>
  );
}
