import React, { useEffect, useRef, useState } from "react";
import * as faceapi from "face-api.js";
import "./PreExamSetup.css";

export default function PreExamSetup({ user, onProceed, onCancel }) {
  const videoRef = useRef(null);
  const [status, setStatus] = useState({
    camera: "checking",
    models: "checking",
    face: "waiting",
    person: "waiting",
    lighting: "waiting",
    identity: "waiting",
  });

  const [canProceed, setCanProceed] = useState(false);
  const [loading, setLoading] = useState(true);
  const [registeredDescriptor, setRegisteredDescriptor] = useState(null);
  const [attempts, setAttempts] = useState(0);
  const intervalRef = useRef(null);

  // -----------------------------
  // STEP 1: Load models and camera
  // -----------------------------
  useEffect(() => {
    initSetup();
    return () => stopCamera();
  }, []);

  const initSetup = async () => {
    try {
      setLoading(true);
      const MODEL_URL = process.env.PUBLIC_URL + "/models";

      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
      ]);
      updateStatus("models", "done");

      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      updateStatus("camera", "done");

      await loadRegisteredDescriptor();
      setLoading(false);

      intervalRef.current = setInterval(runChecks, 1500);
    } catch (err) {
      console.error("Initialization error:", err);
      updateStatus("camera", "error");
      updateStatus("models", "error");
      setLoading(false);
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
    if (intervalRef.current) clearInterval(intervalRef.current);
  };

  // ------------------------------------
  // STEP 2: Load stored descriptor
  // ------------------------------------
  const loadRegisteredDescriptor = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://localhost:4000/api/auth/get-face", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) throw new Error("Face data not found");
      const data = await res.json();

      if (data.faceDescriptor) {
        setRegisteredDescriptor(new Float32Array(data.faceDescriptor));
        console.log("‚úÖ Face descriptor loaded from server");
      } else {
        throw new Error("Invalid face data");
      }
    } catch (err) {
      console.error("Error loading descriptor:", err);
      updateStatus("identity", "error");
    }
  };

  // ------------------------------------
  // STEP 3: Perform all checks
  // ------------------------------------
  const runChecks = async () => {
    if (!videoRef.current || !registeredDescriptor) return;

    try {
      const detection = await faceapi
        .detectAllFaces(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptors();

      if (!detection.length) {
        updateStatus("face", "waiting");
        return;
      } else {
        updateStatus("face", "done");
      }

      if (detection.length === 1) {
        updateStatus("person", "done");
      } else {
        updateStatus("person", "error");
        return;
      }

      // Lighting check
      const lightingOK = await checkLighting(videoRef.current);
      updateStatus("lighting", lightingOK ? "done" : "error");

      // Identity verification
      const match = faceapi.euclideanDistance(
        detection[0].descriptor,
        registeredDescriptor
      );
      console.log("Face match distance:", match);

      if (match < 0.45) {
        updateStatus("identity", "done");
        setAttempts(prev => prev + 1);
      } else {
        updateStatus("identity", "error");
      }

      // All checks done successfully for 3 cycles
      if (
        Object.values(status).every(s => s === "done") &&
        attempts >= 3
      ) {
        setCanProceed(true);
        clearInterval(intervalRef.current);
      }
    } catch (err) {
      console.error("Check error:", err);
    }
  };

  // ------------------------------------
  // Lighting estimation
  // ------------------------------------
  const checkLighting = async video => {
    try {
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height).data;

      let totalBrightness = 0;
      for (let i = 0; i < imageData.length; i += 4) {
        const brightness =
          0.299 * imageData[i] + 0.587 * imageData[i + 1] + 0.114 * imageData[i + 2];
        totalBrightness += brightness;
      }
      const avgBrightness = totalBrightness / (canvas.width * canvas.height);
      return avgBrightness > 60 && avgBrightness < 220;
    } catch {
      return false;
    }
  };

  const updateStatus = (key, value) => {
    setStatus(prev => ({ ...prev, [key]: value }));
  };

  // ------------------------------------
  // UI
  // ------------------------------------
  const renderIcon = s => {
    if (s === "done") return "‚úÖ";
    if (s === "error") return "‚ùå";
    return "‚è≥";
  };

  return (
    <div className="preexam-container">
      <div className="preexam-card">
        <h2>üßæ Pre-Exam Security Setup</h2>
        <p className="subtitle">Complete these checks before starting the exam</p>

        <div className="video-container">
          <video
            ref={videoRef}
            autoPlay
            muted
            className="exam-video"
            onLoadedMetadata={() => {
              if (videoRef.current) {
                videoRef.current.width = videoRef.current.videoWidth;
                videoRef.current.height = videoRef.current.videoHeight;
              }
            }}
          />
        </div>

        <div className="checklist">
          <p>{renderIcon(status.camera)} Camera connected</p>
          <p>{renderIcon(status.models)} Detection models loaded</p>
          <p>{renderIcon(status.face)} Waiting for face detection...</p>
          <p>{renderIcon(status.person)} Checking for single person...</p>
          <p>{renderIcon(status.lighting)} Checking lighting conditions...</p>
          <p>{renderIcon(status.identity)} Verifying your identity...</p>
        </div>

        <div className="action-buttons">
          <button
            className="btn-start"
            disabled={!canProceed}
            onClick={() => {
              stopCamera();
              onProceed && onProceed(user.examId);
            }}
          >
            {canProceed ? "Start Exam" : "Complete Checks First"}
          </button>
          <button className="btn-cancel" onClick={() => {
            stopCamera();
            onCancel && onCancel();
          }}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
