import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useParams, useNavigate } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import StudentDashboard from './components/StudentDashboard';
import AdminDashboard from './components/AdminDashboard';
import ExamPage from './components/ExamPage';
import ResultPage from './components/ResultPage';
import MyResultsPage from './components/MyResultsPage';
import ExamCreator from './components/ExamCreator';
import FaceRegistration from './components/FaceRegistration';
import PreExamSetup from './components/PreExamSetup';
import { setAuthToken } from './api';
import './styles.css';

function ProtectedRoute({ user, allowedRoles, children }) {
  if (!user) {
    return <Navigate to="/" replace />;
  }
  
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    return <Navigate to="/unauthorized" replace />;
  }
  
  return children;
}

function UnauthorizedPage() {
  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      textAlign: 'center',
      padding: '2rem'
    }}>
      <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>ðŸš«</h1>
      <h2 style={{ color: '#1f2937', marginBottom: '0.5rem' }}>Access Denied</h2>
      <p style={{ color: '#6b7280', marginBottom: '2rem' }}>
        You don't have permission to access this page.
      </p>
      <button 
        onClick={() => window.location.href = '/'}
        style={{
          padding: '0.75rem 2rem',
          background: '#2563eb',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '1rem',
          fontWeight: '600',
          cursor: 'pointer',
          transition: 'all 0.3s ease'
        }}
      >
        Go to Home
      </button>
    </div>
  );
}

// Wrapper component to handle pre-exam setup flow
function ExamPageWrapper({ user, onLogout }) {
  const { examId } = useParams();
  const [showSetup, setShowSetup] = useState(true);
  const [examData, setExamData] = useState(null);
  const [setupData, setSetupData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadExamData();
  }, [examId]);

  const loadExamData = async () => {
    try {
      const res = await fetch(`http://localhost:4000/api/exam/paper/${examId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });

      if (!res.ok) throw new Error("Failed to load exam");
      const data = await res.json();
      setExamData(data);
    } catch (err) {
      console.error("Error loading exam:", err);
      alert("Failed to load exam");
      navigate("/student/dashboard");
    }
  };

  const handleSetupComplete = (data) => {
    console.log("âœ… Pre-exam setup completed");
    setSetupData(data);
    setShowSetup(false);
  };

  const handleSetupCancel = () => {
    navigate("/student/dashboard");
  };

  // Show PreExamSetup first
  if (showSetup) {
    return (
      <PreExamSetup
        user={user}
        examId={examId}
        examDetails={examData}
        onProceed={handleSetupComplete}
        onCancel={handleSetupCancel}
      />
    );
  }

  // Then show ExamPage after setup is complete
  return <ExamPage user={user} onLogout={onLogout} setupData={setupData} />;
}

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    if (token && storedUser) {
      try {
        const userData = JSON.parse(storedUser);
        setAuthToken(token);
        setUser(userData);
      } catch (err) {
        console.error('Error parsing user data:', err);
        handleLogout();
      }
    }
    setLoading(false);
  }, []);

  const handleLogin = (token, userData) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setAuthToken(token);
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setAuthToken(null);
    setUser(null);
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            user ? (
              user.role === 'admin' ? 
                <Navigate to="/admin/dashboard" replace /> : 
                <Navigate to="/student/dashboard" replace />
            ) : (
              <LoginPage onLogin={handleLogin} />
            )
          }
        />
        
        <Route 
          path="/register" 
          element={
            user ? 
              <Navigate to="/" replace /> : 
              <RegisterPage />
          } 
        />
        
        <Route path="/unauthorized" element={<UnauthorizedPage />} />

        {/* Face Registration Route for Students */}
        <Route
          path="/face-registration"
          element={
            <ProtectedRoute user={user} allowedRoles={['student']}>
              <FaceRegistration 
                user={user} 
                onComplete={() => window.location.href = '/student/dashboard'}
              />
            </ProtectedRoute>
          }
        />

        {/* Student Routes */}
        <Route
          path="/student/dashboard"
          element={
            <ProtectedRoute user={user} allowedRoles={['student']}>
              <StudentDashboard user={user} onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        
        {/* Exam Route with Pre-Setup Wrapper */}
        <Route
          path="/exam/:examId"
          element={
            <ProtectedRoute user={user} allowedRoles={['student']}>
              <ExamPageWrapper user={user} onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        
        <Route
          path="/student/result/:submissionId"

          element={
            <ProtectedRoute user={user} allowedRoles={['student']}>
              <ResultPage onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        
        <Route
          path="/my-results"
          element={
            <ProtectedRoute user={user} allowedRoles={['student']}>
              <MyResultsPage user={user} />
            </ProtectedRoute>
          }
        />

        {/* Admin Routes */}
        <Route
          path="/admin/dashboard"
          element={
            <ProtectedRoute user={user} allowedRoles={['admin']}>
              <AdminDashboard user={user} onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        
        <Route
          path="/admin/create-exam"
          element={
            <ProtectedRoute user={user} allowedRoles={['admin']}>
              <ExamCreator user={user} />
            </ProtectedRoute>
          }
        />
        
        <Route
          path="/admin/submission/:submissionId"
          element={
            <ProtectedRoute user={user} allowedRoles={['admin']}>
              <ResultPage isAdmin={true} onLogout={handleLogout} />
            </ProtectedRoute>
          }
        />
        <Route
  path="/student/result/:submissionId"
  element={
    <ProtectedRoute user={user} allowedRoles={['student']}>
      <ResultPage onLogout={handleLogout} />
    </ProtectedRoute>
  }
/>


        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
